export const fetchFile = (bucketName, fileId) => {
  return {
    type: "REQUEST_FILE",
    route: `/api/uploads/${bucketName}/${fileId}`,
    method: "GET"
  };
};

export const uploadFile = (file, ref) => {
  let formData = new FormData();
  console.log(file);

  let temp = file.uri.split("/");
  formData.append("file", {
    uri: file.uri,
    name: temp[temp.length - 1],
    type: "image/jpeg"
  });
  console.log(formData);

  return {
    type: "REQUEST_UPLOAD_FILE",
    route: `uploads/${ref}`,
    payload: formData,
    method: "POST"
  };
};
