export const notifyUser = (notification) => {
  return {
    type: "NOTIFY_USER",
    notification,
  };
};

export const askUserInput = (input) => {
  return {
    type: "NOTIFY_USER",
    input,
  };
};

export const resetNotification = () => {
  return {
    type: "RESET_NOTIFICATION",
  };
};
