import React, { useState } from "react";
import { connect } from "react-redux";
import { View, Text, StyleSheet } from "react-native";
import { Input, Button, Icon, Divider } from "react-native-elements";
import { signUp } from "../actions/authActions";
import { notifyUser } from "../actions/notifyActions";

const SignUpScreen = ({ switchPanel, signUp, auth }) => {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmation, setConfirmation] = useState("");
  const [errorMessage, setErrorMessage] = useState({});

  const handleSignUp = async () => {
    if (!email) {
      return setErrorMessage({ mail: "Mél manquant." });
    } else if (!password) {
      return setErrorMessage({ password: "Mot de passe manquant." });
    } else if (password !== confirmation) {
      return setErrorMessage({
        confirmation: "Veuillez retaper la confirmation de votre mot de passe."
      });
    } else if (!firstname) {
      return setErrorMessage({ firstname: "Mot de passe manquant." });
    } else if (!lastname) {
      return setErrorMessage({ lastname: "Mot de passe manquant." });
    }

    signUp({ email, password });
  };

  return (
    <View style={styles.container}>
      <Text style={{ height: 40, color: "red" }}>{errorMessage}</Text>
      <Input
        placeholder="Prénom"
        value={firstname}
        onChangeText={value => setFirstname(value)}
        label="Prénom"
      />
      <Input
        placeholder="Nom"
        value={lastname}
        onChangeText={value => setLastname(value)}
        label="Nom"
      />
      <Input
        placeholder="Email"
        leftIcon={<Icon name="email" type="material-community" />}
        value={email}
        onChangeText={value => setEmail(value)}
        label="Email"
      />
      <Input
        placeholder="Mot de passe"
        leftIcon={<Icon name="lock" />}
        label="Mot de passe"
        secureTextEntry
        value={password}
        onChangeText={value => setPassword(value)}
      />
      <Input
        placeholder="Mot de passe"
        leftIcon={
          <Icon
            name={confirmation === password ? "check" : "close"}
            color={confirmation === password ? "green" : "red"}
          />
        }
        label="Confirmation"
        secureTextEntry
        value={confirmation}
        leftIconContainerStyle={{ marginRight: 12 }}
        containerStyle={{ marginBottom: 40, marginTop: 8 }}
        onChangeText={value => setConfirmation(value)}
      />
      <Button
        title="S'inscrire"
        onPress={handleSignUp}
        loading={auth.isSigningUp}
        containerStyle={{ marginBottom: 20, marginTop: 8 }}
      />
      <Button
        title="Déjà enregistré? Je me connecte."
        type="clear"
        onPress={switchPanel}
        containerStyle={{ marginBottom: 30 }}
      />
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth
});

const mapDispatchToProps = dispatch => {
  return {
    signUp: credentials => dispatch(signUp(credentials)),
    notifyUser: notification => dispatch(notifyUser(notification))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SignUpScreen);

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 12
  },
  textInput: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginTop: 8
  }
});
