import React from "react";
import { View } from "react-native";
import AppNavbar from "../AppNavbar";
import { createStackNavigator } from "@react-navigation/stack";
import Lobby from "./MessagesLobbyScreen";
import Room from "./MessagesRoomScreen";

const Stack = createStackNavigator();

const MessagesScreen = ({ navigation }) => {
  return (
    <View style={{ flex: 1 }}>
      <AppNavbar title="Messagerie" navigation={navigation} />
      <Stack.Navigator initialRouteName="Lobby" headerMode="screen">
        <Stack.Screen
          name="Lobby"
          component={Lobby}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="Room"
          component={Room}
          options={({ route }) => ({
            title:
              route.params.conversation.title +
              " - " +
              route.params.job.employmentType,
            headerStatusBarHeight: 0
          })}
        />
      </Stack.Navigator>
    </View>
  );
};

export default MessagesScreen;
