import * as FileSystem from "expo-file-system";
import { Platform } from "react-native";

export const fetchFile = (bucketName, fileId) => {
  return {
    type: "REQUEST_FILE",
    route: `files/${bucketName}/${fileId}`,
    method: "GET",
  };
};

export const deleteFile = (bucketName, fileId) => {
  return {
    type: "REQUEST_DELETE_FILE",
    route: `files/${bucketName}/${fileId}`,
    method: "DELETE",
    successNotification: {
      message: "Votre document a bien été supprimé de la base de données.",
      variant: "success",
      timeout: 2000,
    },
    errorNotification: {
      message: "Cela n'a pas marché... Essayez de nouveau.",
      variant: "error",
      timeout: 2000,
    },
  };
};

export const uploadFile = (file, bucketName, name) => {
  let formData = new FormData();

  if ((Platform.OS = "web")) {
    let blob = dataURLtoBlob(file.uri);
    formData.append("file", blob);
  } else {
    formData.append("file", new File(file, name));
    //  formData.append("file", FileSystem.readAsStringAsync(file.uri));
  }

  name && formData.append("name", name);

  return {
    type: "REQUEST_UPLOAD_FILE",
    route: `files/${bucketName}`,
    payload: formData,
    method: "POST",
    errorNotification: {
      message: "Cela n'a pas marché... Essayez de nouveau.",
      variant: "error",
      timeout: 2000,
    },
  };
};
