export const notifyUser = (notification) => {
  return {
    type: "NOTIFY_USER",
    notification,
  };
};

export const askUserInput = (action, message) => {
  return {
    type: "ASK_USER_INPUT",
    form: {
      action,
      message,
    },
  };
};

export const resetOverlay = () => {
  return {
    type: "RESET_OVERLAY",
  };
};
