import React, { useContext, useState } from "react";
import { Avatar, Icon, ThemeContext } from "react-native-elements";
import { connect } from "react-redux";
import { logOut } from "../actions/authActions";
import { View, Text } from "react-native";
import Constants from "expo-constants";
import { LinearGradient } from "expo-linear-gradient";

// import { navigate, openDrawer } from "../RootNavigation";

const AppNavbar = ({
  auth,
  navigation,
  leftButton,
  title,
  logOut,
  noRightButton
}) => {
  const [showRightMenu, setShowRightMenu] = useState(false);
  const { theme } = useContext(ThemeContext);

  const LeftButtonComponent = () => {
    switch (leftButton) {
      case "profile":
        return (
          <Avatar
            // source={require("../../assets/FaceApp_1575129220146.jpg")}
            title={
              auth.user &&
              auth.user.firstname
                .charAt(0)
                .concat(auth.user.lastname.charAt(0))
                .toUpperCase()
            }
            onPress={() => navigation.openDrawer()}
          />
        );
      case "back":
        return (
          <Icon
            // source={require("../../assets/FaceApp_1575129220146.jpg")}
            name="arrow-left"
            color="white"
            onPress={() => navigation.navigate("Main")}
          />
        );
      default:
        return (
          <Avatar
            // source={require("../../assets/FaceApp_1575129220146.jpg")}
            title={
              auth.user &&
              auth.user.firstname
                .charAt(0)
                .concat(auth.user.lastname.charAt(0))
                .toUpperCase()
            }
            onPress={() => navigation.openDrawer()}
          />
        );
    }
  };

  const CenterComponent = () => {
    return (
      <View>
        <Text
          style={{
            color: "white",
            fontSize: 20,
            textAlign: "center"
          }}
        >
          {title}
        </Text>
      </View>
    );
  };

  const RightComponent = () => {
    if (noRightButton) {
      return <View></View>;
    } else {
      return (
        <View>
          <Icon name="logout" color="white" onPress={() => logOut()} />
        </View>
      );
    }
  };

  return (
    // // <View
    // //   style={{
    // //     flexDirection: "row",
    // //     justifyContent: "space-between",
    // //     alignItems: "center",
    // //     backgroundColor: theme.colors.primary,
    // //     paddingTop: Constants.statusBarHeight,
    // //     paddingHorizontal: 10,
    // //     paddingBottom: 10
    // //   }}
    // // >
    <LinearGradient
      colors={[theme.colors.primary, theme.colors.secondary]}
      start={[0.25, 1]}
      end={[0.25, 0]}
      // style={{
      //   position: "absolute",
      //   left: 0,
      //   right: 0,
      //   top: 0,
      //   height: 100
      // }}
      style={{
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
        // backgroundColor: theme.colors.primary,
        paddingTop: Constants.statusBarHeight,
        paddingHorizontal: 10,
        paddingBottom: 10
      }}
    >
      <LeftButtonComponent />
      <CenterComponent />
      <RightComponent />
    </LinearGradient>
    // </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth
});

const mapDispatchToProps = dispatch => {
  return {
    logOut: () => dispatch(logOut())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AppNavbar);
