import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { View, StyleSheet, Text, ActivityIndicator } from "react-native";
import { FlatList, TouchableOpacity } from "react-native-gesture-handler";
import { Avatar, Divider, Icon, Overlay, Button } from "react-native-elements";
import AppNavbar from "./AppNavbar";
import theme from "../Theme.json";
import { fetchUsers } from "../actions/usersActions";
import _ from "lodash";

const PeopleScreen = ({ users, fetchUsers, navigation }) => {
  useEffect(() => {
    function fetchData() {
      fetchUsers();
    }
    fetchData();
  }, []);

  return (
    <View
      style={{
        flex: 1
      }}
    >
      <AppNavbar
        navigation={navigation}
        title="Mon profile"
        leftButton="back"
        noRightButton={true}
      />
      {auth.user ? (
        <View
          style={{
            flex: 1,
            alignContent: "space-between",
            paddingVertical: 10,
            paddingHorizontal: 8,
            backgroundColor: "white"
          }}
        >
          <ScrollView
            style={{
              paddingTop: 40,
              flex: 1,
              paddingHorizontal: 12
            }}
          >
            <LinearGradient
              colors={[theme.colors.primary, theme.colors.secondary]}
              start={[0.25, 1]}
              end={[0, 0]}
              style={{
                flexDirection: "row",
                marginBottom: 20,
                paddingVertical: 8,
                paddingHorizontal: 16,
                borderRadius: 5
              }}
            >
              <View
                style={{
                  flex: 1,
                  alignContent: "center",
                  justifyContent: "center"
                }}
              >
                <Text style={{ fontSize: 20, color: theme.colors.grey5 }}>
                  {_.capitalize(user.firstname)}
                </Text>
                <Text
                  style={{ fontSize: 30, color: "white", fontWeight: "bold" }}
                >
                  {_.capitalize(user.lastname)}
                </Text>
              </View>
              {user.avatar ? (
                <Avatar
                  onEditPress={() => editAvatar()}
                  size="xlarge"
                  containerStyle={{
                    borderColor: "white",
                    borderWidth: 3
                  }}
                  source={{
                    uri: `http://10.0.2.2:8005/api/files/avatar/${user.avatar?.filename}`
                  }}
                  onPress={() => setShowAvatarForm(true)}
                  showEditButton
                />
              ) : (
                <Avatar
                  onEditPress={() => editAvatar()}
                  size="xlarge"
                  containerStyle={{
                    borderColor: "white",
                    borderWidth: 3
                  }}
                  title={user.firstname
                    .charAt(0)
                    .concat(user.lastname.charAt(0))
                    .toUpperCase()}
                  onPress={() => editAvatar()}
                  showEditButton
                />
              )}

              <Modal
                isVisible={showAvatarForm}
                height="auto"
                onBackdropPress={() => setShowAvatarForm(false)}
              >
                <View>
                  <Button title="Changer" onPress={() => editAvatar()} />
                  <Button
                    title="Supprimer"
                    buttonStyle={{
                      backgroundColor: theme.colors.grey0
                    }}
                    onPress={() => deleteFile("avatar", user.avatar?.filename)}
                  />
                </View>
              </Modal>
            </LinearGradient>
            <View style={{ flexDirection: "row" }}>
              <Icon name="briefcase" size={20} color="grey" />
              <Text
                style={{
                  textAlignVertical: "bottom",
                  marginHorizontal: 8,
                  flex: 1
                }}
              >
                {user.jobTitle} chez {user.organisation}
              </Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "flex-start",
                justifyContent: "flex-start"
              }}
            >
              <Icon name="school" size={20} color="grey" />
              <Text
                style={{
                  textAlignVertical: "bottom",
                  marginHorizontal: 8,
                  flex: 1
                }}
              >
                Promotion {user.promo}
              </Text>
            </View>
            <Divider
              style={{
                marginVertical: 8,
                backgroundColor: "grey",
                borderWidth: 1,
                width: 20
              }}
            />
            <View style={{ flexDirection: "row" }}>
              <Text style={{ textAlign: "center", flex: 1, marginEnd: 8 }}>
                {user.description}
              </Text>
            </View>
            <Divider
              style={{
                marginVertical: 8,
                backgroundColor: "grey",
                borderWidth: 1,
                width: 20
              }}
            />
            <View style={{ flexDirection: "row" }}>
              <Icon name="at" size={20} color="grey" />
              <Text
                style={{ textAlignVertical: "bottom", marginStart: 8, flex: 1 }}
              >
                {user.email}
              </Text>
            </View>
          </ScrollView>
          <View>
            <Button
              title="Editer"
              onPress={() => navigation.navigate("Edit")}
            />
          </View>
        </View>
      ) : (
        <View
          style={{ flex: 1, alignContent: "center", justifyContent: "center" }}
        >
          <ActivityIndicator size="large" />
        </View>
      )}
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth,
  users: state.users
});

const mapDispatchToProps = dispatch => {
  return {
    fetchUsers: () => dispatch(fetchUsers())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PeopleScreen);
