export const notifyUser = (notification) => {
  return {
    type: "NOTIFY_USER",
    notification,
  };
};

export const showOverlayForm = (input) => {
  return {
    type: "SHOW_OVERLAY_FORM",
    input,
  };
};

export const resetNotification = () => {
  return {
    type: "RESET_NOTIFICATION",
  };
};
