export const showOverlay = ({ notification, form, menu, timeout }) => {
  return {
    type: "SHOW_OVERLAY",
    payload: {
      notification,
      form,
      menu,
    },
  };
};

export const resetOverlay = () => {
  return {
    type: "RESET_OVERLAY",
  };
};
