import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { View, StyleSheet, Text, ActivityIndicator } from "react-native";
import { FlatList, TouchableOpacity } from "react-native-gesture-handler";
import { Avatar, Divider, Icon, Overlay, Button } from "react-native-elements";
import AppNavbar from "./AppNavbar";
import theme from "../Theme.json";
import { fetchUsers } from "../actions/usersActions";
import _ from "lodash";

const Item = ({ item, navigation }) => {
  const [showMore, setShowMore] = useState(false);

  return (
    <TouchableOpacity
      onPress={() => {
        navigation.navigate("Read", {
          user: item
        });
      }}
    >
      <View
        style={{
          paddingTop: 8,
          paddingHorizontal: 20,
          marginVertical: 4,
          marginHorizontal: 0,
          borderBottomWidth: 1,
          borderBottomColor: theme.colors.grey4,
          paddingVertical: 12,
          flexDirection: "row"
        }}
      >
        <View
          style={{
            alignContent: "center",
            justifyContent: "center",
            alignItems: "center",
            marginRight: 12
          }}
        >
          {item.avatar ? (
            <Avatar
              source={{
                uri: `http://10.0.2.2:8005/api/files/avatar/${item.avatar?.filename}`
              }}
              size="medium"
            />
          ) : (
            <Avatar
              size="medium"
              title={item.firstname[0].concat(item.lastname[0]).toUpperCase()}
            />
          )}
        </View>
        <View>
          <Text
            style={{
              textAlignVertical: "bottom",
              fontWeight: "bold",
              fontSize: 16
            }}
          >
            {_.capitalize(item.firstname)} {_.capitalize(item.lastname)}
          </Text>
          <View style={{ flexDirection: "row", flex: 1 }}>
            <Icon name="account-card-details" size={20} />
            <Text style={{ textAlignVertical: "bottom", marginStart: 8 }}>
              {item.title} chez {item.organisation}
            </Text>
          </View>
          <View style={{ flexDirection: "row", flex: 1 }}>
            <Icon name="school" size={20} />
            <Text style={{ textAlignVertical: "bottom", marginStart: 8 }}>
              SIEE promotion {item.promo}
            </Text>
          </View>
        </View>
      </View>
      <Overlay
        isVisible={showMore}
        width="auto"
        height="auto"
        overlayStyle={{
          marginHorizontal: 12,
          paddingHorizontal: 20,
          borderRadius: 12,
          paddingVertical: 16
        }}
        onBackdropPress={() => setShowMore(false)}
      >
        <View
          style={{
            alignItems: "center"
          }}
        >
          <Text style={{ fontSize: 16, fontWeight: "bold" }}>
            {_.capitalize(item.firstname)} {_.capitalize(item.lastname)}
          </Text>
          <Text>
            {item.title} chez {item.organisation}
          </Text>
          <Text>{item.email}</Text>
          <Divider
            style={{
              marginVertical: 8,
              backgroundColor: "grey"
            }}
          />
          <Text>{item.description}</Text>
        </View>
      </Overlay>
    </TouchableOpacity>
  );
};

const PeopleScreen = ({ users, fetchUsers, navigation }) => {
  useEffect(() => {
    function fetchData() {
      fetchUsers();
    }
    fetchData();
  }, []);

  return (
    <View style={styles.container}>
      {users.isLoaded ? (
        <FlatList
          style={styles.feed}
          data={users.users}
          renderItem={({ item }) => <Item item={item} />}
          keyExtractor={item => item._id}
        />
      ) : (
        <View
          style={{ flex: 1, alignContent: "center", justifyContent: "center" }}
        >
          <ActivityIndicator size="large" />
        </View>
      )}
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth,
  users: state.users
});

const mapDispatchToProps = dispatch => {
  return {
    fetchUsers: () => dispatch(fetchUsers())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PeopleScreen);

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  directory: {
    flex: 1
  },
  item: {
    marginVertical: 10,
    padding: 10
  },
  feed: {
    borderRadius: 0,
    shadowOffset: { width: 10, height: 10 },
    shadowColor: "black",
    shadowOpacity: 0.3,
    elevation: 2,
    backgroundColor: "white"
  }
});
