const initialState = {
  isLoggingIn: false,
  isLoggingOut: false,
  isFetchingUserInfo: false,
  isLoggedIn: false,
  loggedInAt: null,
  user: null,
  userInfoUpdatedAt: null,
  userInfoError: null,
  authError: null,
  logOutError: null,
  isSigningUp: false,
  token: null,
  userInfoUpdatedAt: null
};

export default function(state = initialState, action) {
  switch (action.type) {
    case "REQUEST_SIGN_UP":
      return {
        ...state,
        isSigningUp: true
      };
    case "REQUEST_SIGN_UP:SUCCESS":
      return {
        ...state,
        isSigningUp: false,
        user: action.user,
        token: action.token,
        loggedInAt: action.receivedAt,
        isLoggedIn: true,
        userInfoUpdatedAt: action.receivedAt
      };
    case "REQUEST_SIGN_UP:ERROR":
      return {
        ...state,
        isSigningUp: false
      };
    case "REQUEST_LOG_IN":
      return {
        ...state,
        isLoggingIn: true
      };
    case "REQUEST_LOG_IN:SUCCESS":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        loggedInAt: action.receivedAt,
        userInfoUpdatedAt: action.receivedAt,
        ...action.data
      };
    case "REQUEST_LOG_OUT":
      return {
        ...state,
        isloggingOut: true
      };
    case "REQUEST_LOG_IN:ERROR":
      return {
        ...state,
        authError: action.error,
        isLoggedIn: false,
        isLoggingIn: false
      };
    case "REQUEST_LOG_OUT:SUCCESS":
      return initialState;
    case "REQUEST_UPDATE_USER":
      return {
        ...state,
        isUpdatingUser: true
      };
    case "REQUEST_UPDATE_USER:SUCCESS":
      return {
        ...state,
        isUpdatingUser: false,
        userInfoUpdatedAt: action.receivedAt,
        ...action.data
      };
    case "REQUEST_UPDATE_USER:ERROR":
      return {
        ...action.data,
        isUpdatingUser: false
      };
    default:
      return state;
  }
}
