import React, { useContext, useState } from "react";
import { connect, useDispatch, useSelector } from "react-redux";
import { Text, View, Dimensions, TextInput } from "react-native";
import { Icon, ThemeContext, Button } from "react-native-elements";
import { resetOverlay } from "../actions/overlayAction";
import * as RootNavigation from "../RootNavigation.js";
import { TouchableOpacity } from "react-native-gesture-handler";

const Notifier = ({ notification }) => {
  const { theme } = useContext(ThemeContext);

  return (
    <View
      style={{
        backgroundColor: "white",
        borderRadius: 10,
        justifyContent: "center",
        alignItems: "center",
        paddingHorizontal: 12,
        paddingVertical: 8,
        elevation: 8,
      }}
    >
      <Icon
        containerStyle={{ marginBottom: 8 }}
        size={30}
        name={
          notification.variant === "success"
            ? "check-circle-outline"
            : notification.variant === "warning"
            ? "alert-circle-outline"
            : notification.variant === "error"
            ? "close-circle-outline"
            : notification.icon
        }
        color={
          notification.variant === "success"
            ? "green"
            : notification.variant === "warning"
            ? "yellow"
            : notification.variant === "error"
            ? "red"
            : notification.variant === "error"
            ? "blue"
            : theme.colors.grey1
        }
      />
      {notification.message && (
        <Text style={{ fontSize: 14 }}>{notification.message}</Text>
      )}
    </View>
  );
};

const InputForm = ({ form }) => {
  if (!form) return null;
  const { theme } = useContext(ThemeContext);
  const [inputValue, setInputValue] = useState("");
  const dispatch = useDispatch();

  return (
    <View
      style={{
        backgroundColor: "white",
        borderRadius: 10,
        justifyContent: "center",
        alignItems: "center",
        paddingHorizontal: 12,
        paddingVertical: 8,
        elevation: 8,
      }}
    >
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "center",
          alignContent: "center",
          marginTop: 8,
        }}
      >
        {form.message && <Text style={{ fontSize: 14 }}>{form.message}</Text>}
      </View>
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "center",
          alignContent: "center",
          marginTop: 8,
        }}
      >
        <TextInput
          value={inputValue}
          multiline
          placeholder="Ecrivez ici."
          placeholderTextColor="grey"
          onChangeText={(text) => setInputValue(text)}
          style={{
            backgroundColor: theme.colors.grey5,
            borderColor: theme.colors.grey4,
            borderWidth: 1,
            borderRadius: 5,
            paddingVertical: 4,
            paddingHorizontal: 4,
            maxHeight: 80,
            alignItems: "center",
            alignContent: "center",
            justifyContent: "center",
          }}
        />
        <Button
          containerStyle={{ height: 30 }}
          icon={
            <Icon
              name="send"
              size={20}
              color={inputValue ? theme.colors.primary : theme.colors.grey2}
            />
          }
          disabled={!inputValue}
          type="clear"
          onPress={() => {
            dispatch(
              form.action({
                [form.inputName]: inputValue,
                ...form.actionParams,
              })
            );
            dispatch(resetOverlay());
          }}
        />
      </View>
    </View>
  );
};

const Menu = ({ menu }) => {
  if (!form) return null;
  const { theme } = useContext(ThemeContext);
  const dispatch = useDispatch();

  return (
    <View
      style={{
        backgroundColor: "white",
        borderRadius: 10,
        justifyContent: "center",
        alignItems: "center",
        paddingHorizontal: 12,
        paddingVertical: 8,
        elevation: 8,
      }}
    >
      {form.buttons?.map((button) => {
        return (
          <Button title={button.title} onPress={dispatch(button.action())} />
        );
      })}
    </View>
  );
};

const CustomOverlay = () => {
  const dispatch = useDispatch();
  const overlay = useSelector((state) => state.overlay);
  console.log(overlay);
  if (overlay.show === false) return null;
  const { theme } = useContext(ThemeContext);

  overlay.timeout &&
    setTimeout(() => {
      dispatch(resetOverlay());
      overlay.redirect && RootNavigation.navigate(overlay.redirect, {});
      overlay.dispatchCallback && dispatch({ type: overlay.dispatchCallback });
    }, overlay.timeout);

  return (
    <View
      style={{
        position: "absolute",
        height: Dimensions.get("window").height,
        width: Dimensions.get("window").width,
        backgroundColor: theme.colors.grey6,
        alignContent: "center",
        justifyContent: "center",
        alignItems: "stretch",
      }}
    >
      <TouchableOpacity
        containerStyle={{ flex: 1, minHeight: 12 }}
        style={{
          flex: 1,
          minHeight: 12,
        }}
        onPress={() => dispatch(resetOverlay())}
      />
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <TouchableOpacity
          containerStyle={{ flex: 1, minWidth: 12 }}
          style={{
            flex: 1,
            minWidth: 12,
          }}
          onPress={() => dispatch(resetOverlay())}
        />
        {overlay.notification && (
          <Notifier notification={overlay.notification} />
        )}
        {overlay.form && <InputForm form={overlay.form} />}
        {overlay.menu && <Menu menu={overlay.menu} />}
        <TouchableOpacity
          containerStyle={{ flex: 1, minWidth: 12 }}
          style={{
            flex: 1,
            minWidth: 12,
          }}
          onPress={() => dispatch(resetOverlay())}
        />
      </View>
      <TouchableOpacity
        containerStyle={{ flex: 1, minHeight: 12 }}
        style={{
          flex: 1,
          minHeight: 12,
        }}
        onPress={() => dispatch(resetOverlay())}
      />
    </View>
  );
};

const mapStateToProps = (state) => ({
  notification: state.notification,
});

const mapDispatchToProps = (dispatch) => {
  return {
    reset: () => dispatch(resetOverlay()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CustomOverlay);
