import React, { useEffect } from "react";
import { connect } from "react-redux";
import { Avatar, Divider, Icon, Button } from "react-native-elements";
import { View, Text, ScrollView } from "react-native";
import * as ImagePicker from "expo-image-picker";
import * as Permissions from "expo-permissions";
import { uploadFile, fetchFile } from "../actions/filesActions";
import { LinearGradient } from "expo-linear-gradient";
import theme from "../Theme.json";
import _ from "lodash";
import AppNavbar from "./AppNavbar";

const ReadProfileScreen = ({ uploadFile, auth, navigation, fetchFile }) => {
  useEffect(() => {
    function fetchData() {
      auth.user.profilePicture &&
        fetchFile("profile-picture", auth.user.profilePicture);
    }
    fetchData();
  }, []);

  const editPP = () => {
    const takePicture = async () => {
      await Permissions.askAsync(Permissions.CAMERA);
      await ImagePicker.launchCameraAsync({
        allowsEditing: false
      });
    };

    const selectPicture = async () => {
      // await Permissions.askAsync(Permissions.CAMERA_ROLL);
      await ImagePicker.requestCameraRollPermissionsAsync();

      const picture = await ImagePicker.launchImageLibraryAsync({
        allowsEditing: true
      });
      console.log(picture);

      !picture.cancelled && uploadFile(picture, "profile-pictures");
    };
    selectPicture();
  };

  const { user } = auth;
  return (
    <View
      style={{
        flex: 1
      }}
    >
      <AppNavbar
        title="Messagerie"
        navigation={navigation}
        title="Mon profile"
        leftButton="back"
        noRightButton={true}
      />
      {auth.user ? (
        <View
          style={{
            flex: 1,
            alignContent: "space-between",
            paddingVertical: 10,
            paddingHorizontal: 8,
            backgroundColor: "white"
          }}
        >
          <ScrollView
            style={{
              paddingTop: 40,
              flex: 1,
              paddingHorizontal: 12
            }}
          >
            <LinearGradient
              colors={[theme.colors.primary, theme.colors.secondary]}
              start={[0.25, 1]}
              end={[0, 0]}
              style={{
                flexDirection: "row",
                marginBottom: 20,
                paddingVertical: 8,
                paddingHorizontal: 16,
                borderRadius: 5
              }}
            >
              <View
                style={{
                  flex: 1,
                  alignContent: "center",
                  justifyContent: "center"
                }}
              >
                <Text style={{ fontSize: 20, color: theme.colors.grey5 }}>
                  {_.capitalize(user.firstname)}
                </Text>
                <Text
                  style={{ fontSize: 30, color: "white", fontWeight: "bold" }}
                >
                  {_.capitalize(user.lastname)}
                </Text>
              </View>
              {/* <Avatar
                onEditPress={() => editPP()}
                size="xlarge"
                containerStyle={{
                  borderColor: "white",
                  borderWidth: 3
                }}
                source={auth.user.profilePictureReadable}
                title={user.firstname
                  .charAt(0)
                  .concat(user.lastname.charAt(0))
                  .toUpperCase()}
                onPress={() => editPP(true)}
                showEditButton
              /> */}
            </LinearGradient>
            <View style={{ flexDirection: "row" }}>
              <Icon name="briefcase" size={20} color="grey" />
              <Text
                style={{
                  textAlignVertical: "bottom",
                  marginHorizontal: 8,
                  flex: 1
                }}
              >
                {user.title} chez {user.organisation}
              </Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "flex-start",
                justifyContent: "flex-start"
              }}
            >
              <Icon name="school" size={20} color="grey" />
              <Text
                style={{
                  textAlignVertical: "bottom",
                  marginHorizontal: 8,
                  flex: 1
                }}
              >
                Promotion {user.promo}
              </Text>
            </View>
            <Divider
              style={{
                marginVertical: 8,
                backgroundColor: "grey",
                borderWidth: 1,
                width: 20
              }}
            />
            <View style={{ flexDirection: "row" }}>
              <Text style={{ textAlign: "center", flex: 1, marginEnd: 8 }}>
                "{user.description}"
              </Text>
            </View>
            <Divider
              style={{
                marginVertical: 8,
                backgroundColor: "grey",
                borderWidth: 1,
                width: 20
              }}
            />
            <View style={{ flexDirection: "row" }}>
              <Icon name="at" size={20} color="grey" />
              <Text
                style={{ textAlignVertical: "bottom", marginStart: 8, flex: 1 }}
              >
                {user.email}
              </Text>
            </View>
          </ScrollView>
          <View>
            <Button
              title="Editer"
              onPress={() => navigation.navigate("Edit")}
            />
          </View>
        </View>
      ) : (
        <View
          style={{ flex: 1, alignContent: "center", justifyContent: "center" }}
        >
          <ActivityIndicator size="large" />
        </View>
      )}
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth
});

const mapDispatchToProps = dispatch => {
  return {
    uploadFile: (file, type) => dispatch(uploadFile(file, type)),
    fetchFile: (bucketName, id) => dispatch(fetchFile(bucketName, id))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ReadProfileScreen);
