import React, { useState } from "react";
import theme from "../Theme.json";
import { connect } from "react-redux";
import { View, StyleSheet, Switch, Text, ScrollView } from "react-native";
import { ButtonGroup, Input, Button, Icon } from "react-native-elements";
import AppNavbar from "./AppNavbar";
import { createJob } from "../actions/jobsActions";
import { createPost } from "../actions/postsActions";

const PostScreen = ({ createJob, createPost, navigation }) => {
  const [index, setIndex] = useState(0);
  const [uploading, setUploading] = useState(false);

  const [errors, setErrors] = useState({});

  // Post
  const [tags, setTags] = useState();
  const [postText, setPostText] = useState();

  // Job
  const [employmentType, setEmploymentType] = useState();
  const [asap, setAsap] = useState(false);
  const [organisation, setOrganisation] = useState();
  const [city, setCity] = useState();
  const [salary, setSalary] = useState();
  const [jobTitle, setJobTitle] = useState();
  const [jobDesc, setJobDesc] = useState();
  const [minExp, setMinExp] = useState();
  const [contact, setContact] = useState();

  const buttons = ["Je recrute", "Je poste un sujet"];

  const uploadJobOffer = async () => {
    const jobOffer = {
      employmentType,
      asap,
      organisation,
      city,
      salary,
      jobDesc,
      jobTitle,
      minExp,
      contact
    };

    // {
    //   if (!employmentType) {
    //     return setErrors({
    //       employmentType: "Ce champs est obligatoire."
    //     });
    //   } else if (!organisation) {
    //     return setErrors({
    //       organisation: "Ce champs est obligatoire."
    //     });
    //   } else if (!city) {
    //     return setErrors({
    //       city: "Ce champs est obligatoire."
    //     });
    //   } else if (!jobDesc) {
    //     return setErrors({
    //       jobDesc: "Ce champs est obligatoire."
    //     });
    //   } else if (!jobTitle) {
    //     return setErrors({
    //       jobTitle: "Ce champs est obligatoire."
    //     });
    //   } else if (!minExp) {
    //     return setErrors({
    //       minExp: "Ce champs est obligatoire."
    //     });
    //   }
    // }
    createJob(jobOffer);

    navigation.navigate("People");
    navigation.navigate("MyJobs");

    {
      setAsap(false);
      setCity();
      setSalary();
      setContact();
      setErrors({});
      setEmploymentType();
      setOrganisation();
      setJobDesc();
      setJobTitle();
    }
  };

  return (
    <View style={styles.container}>
      <AppNavbar navigation={navigation} title="Publier" />
      <View style={{ flex: 1, backgroundColor: "white" }}>
        {index === 0 && (
          <ScrollView
            style={{
              paddingHorizontal: 12,
              paddingTop: 20,
              paddingBottom: 100
            }}
          >
            <Input
              label="Type de contrat*"
              placeholder="CDI, CDD, Apprentissage..."
              value={employmentType}
              onChangeText={value => setEmploymentType(value)}
              errorMessage={errors.employmentType}
            />
            <Input
              label="Organisation*"
              placeholder="Deloitte, Engie, EY..."
              value={organisation}
              onChangeText={value => setOrganisation(value)}
              errorMessage={errors.organisation}
            />
            <Input
              placeholder="Paris, Shanghai, Lyon..."
              label="Ville*"
              value={city}
              onChangeText={value => setCity(value)}
              errorMessage={errors.city}
            />
            <Input
              placeholder="Auditeur des SI, Pentester junior..."
              label="Intitulé du poste*"
              value={jobTitle}
              onChangeText={value => setJobTitle(value)}
              errorMessage={errors.jobTitle}
            />
            <Input
              label="Description du poste*"
              multiline
              inputStyle={{ height: 200, textAlignVertical: "top" }}
              placeholder="Les missions, les compétences requises, l'employeur..."
              value={jobDesc}
              onChangeText={value => setJobDesc(value)}
              errorMessage={errors.jobDesc}
            />
            <Input
              label="Expérience*"
              placeholder="Années passées dans un poste similaire"
              value={minExp}
              keyboardType="number-pad"
              onChangeText={value => setMinExp(value)}
              errorMessage={errors.minExp}
            />
            <Input
              label="Rémunération"
              placeholder="En €"
              keyboardType="decimal-pad"
              value={salary}
              onChangeText={value => setSalary(value)}
              showSoftInputOnFocus={false}
              errorMessage={errors.salary}
            />
            <View
              style={{
                marginBottom: 12,
                flexDirection: "row",
                display: "flex",
                height: 40
              }}
            >
              <Text
                style={{
                  color: theme.colors.grey3,
                  fontWeight: "bold",
                  paddingLeft: 10,
                  fontSize: 16,
                  flex: 1,
                  textAlignVertical: "center"
                }}
              >
                Poste à pourvoir dès que possible
              </Text>
              <Switch value={asap} onValueChange={value => setAsap(value)} />
            </View>
            <Input
              label="Contact"
              placeholder="Email"
              value={contact}
              onChangeText={value => setContact(value)}
              errorMessage={errors.contact}
            />
            <Button
              title="Poster l'offre d'emploi"
              buttonStyle={{ height: 50, marginBottom: 12 }}
              onPress={() => {
                setUploading(true);
                uploadJobOffer();
              }}
            />
            <View style={{ height: 20 }}></View>
          </ScrollView>
        )}
      </View>
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth
});

const mapDispatchToProps = dispatch => {
  return {
    createJob: job => dispatch(createJob(job)),
    createPost: post => dispatch(createPost(post))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PostScreen);

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  textInput: {
    height: 40,
    width: 0.9,
    borderWidth: 1,
    marginTop: 8
  }
});
