import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { View, StyleSheet, Text, ActivityIndicator } from "react-native";
import { FlatList, TouchableOpacity } from "react-native-gesture-handler";
import { Avatar, Divider, Icon, Overlay, Button } from "react-native-elements";
import AppNavbar from "./AppNavbar";
import theme from "../Theme.json";
import { fetchUsers } from "../actions/usersActions";
import _ from "lodash";
import { fetchFile } from "../actions/filesActions";
import { fetchReq } from "../fetch";

const Item = ({ item }) => {
  // useEffect(() => {
  //   function fetchData() {
  //     item.user.profilePicture &&
  //       fetchFile("profile-picture", item.user.profilePicture);
  //   }
  //   fetchData();
  // }, []);

  const [showMore, setShowMore] = useState(false);
  fetchReq(`uploads/profile-pictures/${item.profilePicture}`);

  return (
    <TouchableOpacity
      onPress={() => {
        setShowMore(true);
      }}
    >
      <View
        style={{
          paddingTop: 8,
          paddingHorizontal: 20,
          marginVertical: 4,
          marginHorizontal: 0,
          borderBottomWidth: 1,
          borderBottomColor: theme.colors.grey4,
          paddingVertical: 12,
          flexDirection: "row"
        }}
      >
        <View
          style={{
            alignContent: "center",
            justifyContent: "center",
            alignItems: "center",
            marginRight: 12
          }}
        >
          {/* <Avatar
            size="medium"
            source={
              item.profilePicture &&
              fetchReq(`uploads/profile-pictures/${item.profilePicture}`)
            }
            title={item.firstname
              .charAt(0)
              .concat(item.lastname.charAt(0))
              .toUpperCase()}
          /> */}
        </View>
        <View>
          <Text
            style={{
              textAlignVertical: "bottom",
              fontWeight: "bold",
              fontSize: 16
            }}
          >
            {_.capitalize(item.firstname)} {_.capitalize(item.lastname)}
          </Text>
          <View style={{ flexDirection: "row", flex: 1 }}>
            <Icon name="account-card-details" size={20} />
            <Text style={{ textAlignVertical: "bottom", marginStart: 8 }}>
              {item.title} chez {item.organisation}
            </Text>
          </View>
          <View style={{ flexDirection: "row", flex: 1 }}>
            <Icon name="school" size={20} />
            <Text style={{ textAlignVertical: "bottom", marginStart: 8 }}>
              SIEE promotion {item.promo}
            </Text>
          </View>
        </View>
      </View>
      <Overlay
        isVisible={showMore}
        width="auto"
        height={500}
        containerStyle={{}}
        overlayStyle={{
          marginHorizontal: 12,
          paddingHorizontal: 20,
          borderRadius: 12,
          paddingVertical: 16
        }}
        onBackdropPress={() => setShowMore(false)}
      >
        <View>
          <View
            style={{
              alignItems: "center",
              flex: 1
            }}
          >
            {/* <Avatar
              source={item.profilePicture}
              title={item.firstname.charAt(0).concat(item.lastname.charAt(0))}
              size="xlarge"
              containerStyle={{
                position: "absolute",
                top: -60,
                borderColor: "white",
                borderWidth: 3
              }}
            /> */}
            <Text style={{ marginTop: 100, fontSize: 16, fontWeight: "bold" }}>
              {item.firstname} {item.lastname}
            </Text>
            <Text>
              {item.title} chez {item.organisation}
            </Text>
            <Text>{item.email}</Text>
            <Divider
              style={{
                marginVertical: 8,
                backgroundColor: "grey"
              }}
            />
            <Text>{item.description}</Text>
          </View>
          <Button title="Contacter" />
        </View>
      </Overlay>
    </TouchableOpacity>
  );
};

const PeopleScreen = ({ users, fetchUsers, navigation }) => {
  useEffect(() => {
    function fetchData() {
      fetchUsers();
    }
    fetchData();
  }, []);

  return (
    <View style={styles.container}>
      <AppNavbar navigation={navigation} title="Annuaire" />
      {/* <FilterBar /> */}
      {users.isLoaded ? (
        <FlatList
          style={styles.feed}
          data={users.users}
          renderItem={({ item }) => <Item item={item} />}
          keyExtractor={item => item._id}
        />
      ) : (
        <View
          style={{ flex: 1, alignContent: "center", justifyContent: "center" }}
        >
          <ActivityIndicator size="large" />
        </View>
      )}
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth,
  users: state.users
});

const mapDispatchToProps = dispatch => {
  return {
    fetchUsers: () => dispatch(fetchUsers())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PeopleScreen);

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  directory: {
    flex: 1
  },
  item: {
    marginVertical: 10,
    padding: 10
  },
  feed: {
    borderRadius: 0,
    shadowOffset: { width: 10, height: 10 },
    shadowColor: "black",
    shadowOpacity: 0.3,
    elevation: 2,
    backgroundColor: "white"
  }
});
