export const logIn = credentials => {
  return {
    type: "REQUEST_LOG_IN",
    route: "/auth/login",
    payload: credentials,
    method: "POST"
  };
};

export const signUp = credentials => {
  return {
    type: "REQUEST_SIGN_UP",
    route: "/auth/signup",
    payload: credentials,
    method: "POST",
    successNotification: {
      message: `Bienvenue ! Rendez-vous à l'onglet "Mon profile" pour éditer vos informations.`,
      variant: "success",
      redirect: "Auth",
      timeout: 3000
    },
    errorNotification: {
      message:
        "Cela n'a pas marché... Peut-être un compte avec ce mail existe-t-il déjà.",
      variant: "error",
      timeout: 2000
    }
  };
};

export const logOut = () => {
  return {
    type: "REQUEST_LOG_OUT",
    route: "/auth/logout",
    method: "GET"
  };
};

export const updateUser = user => {
  return {
    type: "REQUEST_UPDATE_USER",
    method: "PATCH",
    route: "auth",
    payload: user,
    successNotification: {
      message: "Vos informations ont été mises à jour.",
      variant: "success",
      redirect: "Profile",
      timeout: 1000
    },
    errorNotification: {
      message: "Cela n'a pas marché... Essayez de nouveau.",
      variant: "error",
      timeout: 2000
    }
  };
};

export const updateUserPassword = passwords => {
  return {
    type: "REQUEST_UPDATE_USER",
    method: "PATCH",
    route: "auth/password",
    payload: passwords,
    successNotification: {
      message: "Votre mot de passe à été mises à jour.",
      variant: "success",
      redirect: "Profile",
      timeout: 1000
    },
    errorNotification: {
      message: "Cela n'a pas marché... Essayez de nouveau.",
      variant: "error",
      timeout: 2000
    }
  };
};

export const deleteAccount = () => {
  return {
    type: "REQUEST_DELETE_ACCOUNT",
    method: "DELETE",
    route: "auth",
    successNotification: {
      message: "Votre compte a bien été supprimé.",
      variant: "success",
      redirect: "Auth",
      timeout: 1000
    },
    errorNotification: {
      message: "Cela n'a pas marché... Essayez de nouveau.",
      variant: "error",
      timeout: 2000
    }
  };
};
