import React, { useState, useEffect, useCallback, useContext } from "react";
import { connect } from "react-redux";
import {
  View,
  Text,
  StyleSheet,
  ActivityIndicator,
  FlatList,
  TouchableOpacity,
  RefreshControl,
  TextInput,
  Dimensions
} from "react-native";
import {
  SearchBar,
  Avatar,
  ThemeContext,
  Button,
  Icon,
  Overlay
} from "react-native-elements";
import _ from "lodash";
import "moment/locale/fr";
import moment from "moment";
import { fetchUsers } from "../actions/usersActions";
import {
  fetchConversations,
  sendMessage,
  deleteConversation
} from "../actions/chatActions";

const SearchResultItem = ({ item, theme, onSendMessage, navigation, chat }) => {
  const [showMessageForm, setShowMessageForm] = useState(false);
  const [newMessage, setNewMessage] = useState(null);

  return (
    <TouchableOpacity
      onPress={
        chat.interlocutorsIds.includes(item._id)
          ? () =>
              navigation.navigate("Room", {
                conversation_id: chat.convIdsWithPartsIds
                  .filter(
                    element => element.participants.includes(item._id) === true
                  )
                  .filter(conv => conv.participants.length === 2)[0]._id
              })
          : () => setShowMessageForm(true)
      }
    >
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          paddingVertical: 4,
          paddingHorizontal: 8,
          borderBottomColor: theme.colors.grey5,
          borderBottomWidth: 1
        }}
      >
        <View
          style={{
            alignContent: "center",
            justifyContent: "center",
            alignItems: "center",
            marginRight: 12
          }}
        >
          {item.avatar ? (
            <Avatar
              source={{
                uri: `http://10.0.2.2:8005/api/files/avatar/${item.avatar?.filename}`
              }}
              size="small"
            />
          ) : (
            <Avatar
              size="small"
              title={item.firstname[0].concat(item.lastname[0]).toUpperCase()}
            />
          )}
        </View>
        <Text style={{ paddingLeft: 12 }}>
          {_.capitalize(item.firstname) + " " + _.capitalize(item.lastname)}
        </Text>
      </View>
      <Overlay
        isVisible={showMessageForm}
        width="auto"
        containerStyle={{}}
        height={100}
        overlayStyle={{
          marginHorizontal: 12,
          paddingHorizontal: 10,
          borderRadius: 12,
          paddingVertical: 16
        }}
        onBackdropPress={() => {
          setShowMessageForm(false);
          setNewMessage(null);
        }}
      >
        <View style={{ paddingHorizontal: 12 }}>
          <Text style={{ textAlign: "center" }}>
            {_.capitalize(item.firstname) + " " + _.capitalize(item.lastname)}
          </Text>
          <View
            style={{
              flexDirection: "row",
              backgroundColor: "white",
              paddingVertical: 8,
              alignItems: "center",
              justifyContent: "center",
              alignContent: "center",
              width: Dimensions.get("screen").width * 0.8
            }}
          >
            <TextInput
              style={{
                height: 40,
                flex: 1,
                borderBottomWidth: 1
              }}
              onChangeText={text => setNewMessage(text)}
              value={newMessage}
              multiline
              numberOfLines={8}
              placeholder="Ecrivez votre message ici."
            />
            <Button
              icon={
                <Icon
                  name="send"
                  size={20}
                  color={newMessage ? theme.colors.primary : theme.colors.grey2}
                />
              }
              disabled={!newMessage}
              type="clear"
              onPress={() => {
                onSendMessage(newMessage, item._id);
                setNewMessage("");
                setShowMessageForm(false);
              }}
            />
          </View>
        </View>
      </Overlay>
    </TouchableOpacity>
  );
};

const Item = ({
  item,
  navigation,
  auth,
  theme,
  deleteConversation,
  fetchConversations
}) => {
  const [showActionMenu, setShowActionMenu] = useState(false);
  const [deleting, setDeleting] = useState(false);
  const listParticipants = item => {
    const participantsArr = item.participants.filter(
      participant => participant._id !== auth.user._id
    );

    if (participantsArr.length > 1) {
      const participantsList = participantsArr.map(
        participant => participant.firstname
      );
      return participantsList.toString().replace(",", ", ");
    } else {
      return _.capitalize(participantsArr[0].firstname).concat(
        " ",
        _.capitalize(participantsArr[0].lastname)
      );
    }
  };
  const participants = listParticipants(item);

  return (
    <TouchableOpacity
      onPress={() => navigation.navigate("Room", { conversation_id: item._id })}
    >
      <View
        style={{
          paddingHorizontal: 8,
          paddingVertical: 8,
          borderBottomColor: "white",
          borderBottomWidth: 3,
          backgroundColor: "white"
        }}
      >
        <View style={{ flexDirection: "row" }}>
          <View
            style={{
              alignContent: "center",
              justifyContent: "center",
              paddingRight: 8
            }}
          >
            {item.avatar ? (
              <Avatar
                source={{
                  uri: `http://10.0.2.2:8005/api/files/avatar/${item.avatar?.filename}`
                }}
                size="medium"
              />
            ) : (
              <Avatar
                size="medium"
                title={item.firstname[0].concat(item.lastname[0]).toUpperCase()}
              />
            )}
          </View>
          <View
            style={{
              flex: 1,
              justifyContent: "space-between"
            }}
          >
            <View
              style={{
                flexDirection: "row",
                flex: 1,
                justifyContent: "space-between"
              }}
            >
              <Text
                style={{
                  textAlignVertical: "bottom",
                  fontSize: 16,
                  fontWeight: "bold"
                }}
              >
                {participants}
              </Text>
              <Text
                color="grey"
                style={{
                  color: "grey",
                  fontSize: 10,
                  textAlignVertical: "bottom"
                }}
              >
                {_.capitalize(
                  moment(item.lastMessage.sentAt)
                    .locale("fr")
                    .fromNow()
                )}
              </Text>
            </View>
            <View style={{ flexDirection: "row" }}>
              <View
                style={{ alignContent: "center", justifyContent: "center" }}
              >
                {item.lastMessage.sender === auth.user._id ? (
                  <Icon
                    name="arrow-top-right"
                    color={theme.colors.grey1}
                    size={14}
                  />
                ) : (
                  <Icon
                    name="arrow-bottom-left"
                    color={theme.colors.grey3}
                    size={14}
                  />
                )}
              </View>
              <Text
                style={{
                  textAlignVertical: "center",
                  fontSize: 14,
                  flex: 1
                }}
              >
                {item.lastMessage.text}
              </Text>
              <View
                style={{ alignContent: "center", justifyContent: "center" }}
              >
                <Icon
                  name="dots-horizontal"
                  style={{ alignSelf: "flex-end" }}
                  onPress={() => setShowActionMenu(true)}
                />
              </View>
              <Overlay
                isVisible={showActionMenu}
                onBackdropPress={() => setShowActionMenu(false)}
                height="auto"
              >
                <Button
                  title="Supprimer la conversation"
                  loading={deleting}
                  onPress={async () => {
                    setDeleting(true);
                    await deleteConversation(item._id);
                    setDeleting(false);
                    setShowActionMenu(false);
                    fetchConversations();
                  }}
                />
              </Overlay>
            </View>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const ChatLobbyScreen = ({
  navigation,
  fetchConversations,
  fetchUsers,
  chat,
  users,
  auth,
  sendMessage,
  deleteConversation
}) => {
  const [refreshing, setRefreshing] = useState(false);
  const [search, setSearch] = useState(null);
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    function fetchData() {
      fetchConversations();
      fetchUsers();
    }
    fetchData();
  }, []);

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    fetchConversations();
    setRefreshing(false);
  }, [refreshing]);

  const searchProcess = () => {
    const searchResults = [];
    users.users.forEach(user => {
      if (user._id !== auth.user._id) {
        if (
          user.firstname.includes(search.toLowerCase()) === true ||
          user.lastname.includes(search.toLowerCase())
        ) {
          searchResults.push(user);
        }
      }
    });
    return searchResults;
  };

  const onSendMessage = (newMessage, recipient) => {
    if (!newMessage) {
      return;
    }
    sendMessage({
      message: {
        text: newMessage,
        sentAt: new Date()
      },
      recipients: [recipient]
    });
    setSearch(null);
  };

  return (
    <View style={styles.container}>
      <View style={{ backgroundColor: "white", paddingVertical: 4 }}>
        <SearchBar
          placeholder="Chercher un alumni ici..."
          onChangeText={setSearch}
          value={search}
          lightTheme={true}
          platform="android"
          containerStyle={{
            paddingTop: 0,
            height: 40
          }}
        />
        {users.isLoaded && (
          <FlatList
            style={{}}
            data={search && searchProcess()}
            renderItem={({ item }) => (
              <SearchResultItem
                item={item}
                theme={theme}
                onSendMessage={onSendMessage}
                chat={chat}
                navigation={navigation}
              />
            )}
            keyExtractor={item => item._id}
          />
        )}
        {!users.isLoaded && (
          <View
            style={{
              flex: 1,
              alignContent: "center",
              justifyContent: "center"
            }}
          >
            <ActivityIndicator size="small" />
          </View>
        )}
      </View>
      {chat.isLoaded ? (
        chat.conversations.length > 0 ? (
          <FlatList
            style={styles.feed}
            data={chat.conversations}
            renderItem={({ item }) => (
              <Item
                item={item}
                auth={auth}
                navigation={navigation}
                theme={theme}
                deleteConversation={deleteConversation}
                fetchConversations={fetchConversations}
              />
            )}
            keyExtractor={item => item._id}
            refreshControl={
              <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
            }
          />
        ) : (
          <View
            style={{
              flex: 1,
              alignContent: "center",
              justifyContent: "center"
            }}
          >
            <Text style={{ textAlign: "center", color: theme.colors.grey3 }}>
              Aucune conversation
            </Text>
            <Button
              title="Rafraichir"
              type="clear"
              onPress={fetchConversations}
            />
          </View>
        )
      ) : (
        <View
          style={{
            flex: 1,
            alignContent: "center",
            justifyContent: "center"
          }}
        >
          <ActivityIndicator size="large" />
        </View>
      )}
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth,
  chat: state.chat,
  users: state.users
});

const mapDispatchToProps = dispatch => {
  return {
    fetchConversations: () => dispatch(fetchConversations()),
    fetchUsers: () => dispatch(fetchUsers()),
    sendMessage: message => dispatch(sendMessage(message)),
    deleteConversation: conversation_id =>
      dispatch(deleteConversation(conversation_id))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ChatLobbyScreen);
