import React from "react";
import "react-native-gesture-handler";
import { Provider, useSelector, shallowEqual } from "react-redux";
import { View, SafeAreaView, Platform, StatusBar } from "react-native";
import { store, persistor } from "./store/index";
import { PersistGate } from "redux-persist/integration/react";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { ThemeProvider, Icon } from "react-native-elements";
import MyJobs from "./components/MyJobs";
import Jobs from "./components/Jobs";
import Profile from "./components/Profile";
import People from "./components/People";
import Messages from "./components/Messages";
import Auth from "./components/Auth";
import Notifier from "./components/Notifier";
import theme from "./Theme.json";
import { navigationRef, isMountedRef } from "./RootNavigation";
import { createStackNavigator } from "@react-navigation/stack";

const BottomTab = createBottomTabNavigator();
const Drawer = createDrawerNavigator();
const Stack = createStackNavigator();

const MainStack = () => {
  return (
    <BottomTab.Navigator
      initialRouteName="People"
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          switch (route.name) {
            case "Home":
              iconName = "home";
              break;
            case "Jobs":
              iconName = "briefcase";
              break;
            case "People":
              iconName = "account-group";
              break;
            case "Messages":
              iconName = "forum";
              break;
            default:
              break;
          }

          return <Icon name={iconName} size={size} color={color} />;
        }
      })}
      tabBarOptions={{
        activeTintColor: theme.colors.primary,
        inactiveTintColor: "gray"
      }}
    >
      <BottomTab.Screen
        name="People"
        component={People}
        options={{ title: "Alumnis" }}
      />
      <BottomTab.Screen
        name="Jobs"
        component={Jobs}
        options={{ title: "Emplois" }}
      />
      <BottomTab.Screen
        name="Messages"
        component={Messages}
        options={{ title: "Messages" }}
      />
    </BottomTab.Navigator>
  );
};

const AppStack = () => {
  if (state.isLoading) {
    return <SplashScreen />;
  }
  const authContext = React.useMemo(
    () => ({
      signIn: async data => {
        // In a production app, we need to send some data (usually username, password) to server and get a token
        // We will also need to handle errors if sign in failed
        // After getting token, we need to persist the token using `AsyncStorage`
        // In the example, we'll use a dummy token

        dispatch({ type: "SIGN_IN", token: "dummy-auth-token" });
      },
      signOut: () => dispatch({ type: "SIGN_OUT" }),
      signUp: async data => {
        // In a production app, we need to send user data to server and get a token
        // We will also need to handle errors if sign up failed
        // After getting token, we need to persist the token using `AsyncStorage`
        // In the example, we'll use a dummy token

        dispatch({ type: "SIGN_IN", token: "dummy-auth-token" });
      }
    }),
    []
  );
  React.useEffect(() => {
    const bootstrapAsync = async () => {
      let userToken;
      try {
        userToken = await store.getState().auth.user.token;
        console.log(userToken);
      } catch (e) {}
      dispatch({ type: "RESTORE_TOKEN", token: userToken });
    };

    bootstrapAsync();
  }, []);

  const isLoggedIn = useSelector(state => state.auth.isLoggedIn, shallowEqual);
  return (
    <View
      style={{
        flex: 1,
        maxWidth: 500
      }}
    >
      {isLoggedIn ? (
        <Drawer.Navigator initialRouteName="Main" headerMode="screen">
          <Drawer.Screen
            name="Main"
            component={MainStack}
            options={{ title: "Acceuil" }}
          />
          <Drawer.Screen
            name="Profile"
            component={Profile}
            options={{ title: "Mon profil" }}
          />
          <Drawer.Screen
            name="MyJobs"
            component={MyJobs}
            options={{ title: "Mes offres d'emplois" }}
          />
        </Drawer.Navigator>
      ) : (
        <Auth />
      )}
    </View>
  );
};

const App = () => {
  React.useEffect(() => {
    isMountedRef.current = true;

    return () => (isMountedRef.current = false);
  }, []);

  return (
    <Provider store={store}>
      <PersistGate loading={null} persistor={persistor}>
        <ThemeProvider theme={theme}>
          <SafeAreaView
            style={{
              flex: 1,
              flexDirection: "row",
              justifyContent: "center"
            }}
          >
            {Platform.OS === "ios" && <StatusBar barStyle="default" />}
            <NavigationContainer ref={navigationRef}>
              <AppStack />
              <Notifier />
            </NavigationContainer>
          </SafeAreaView>
        </ThemeProvider>
      </PersistGate>
    </Provider>
  );
};

export default App;
