import React, { useEffect, useContext, useState } from "react";
import { connect } from "react-redux";
import { StyleSheet, Text, View, Dimensions, TextInput } from "react-native";
import { Icon, ThemeContext, Button } from "react-native-elements";
import { resetNotification } from "../actions/notifyActions";
import * as RootNavigation from "../RootNavigation.js";
import Constants from "expo-constants";
import { TouchableOpacity } from "react-native-gesture-handler";

const Notifier = ({ notification, reset }) => {
  const { theme } = useContext(ThemeContext);
  notification.message &&
    setTimeout(() => {
      reset();
      notification.redirect &&
        RootNavigation.navigate(notification.redirect, {});
    }, notification.timeout);
  if (notification.show === true) {
    return (
      <View
        style={{
          position: "absolute",
          height: Dimensions.get("window").height,
          width: Dimensions.get("window").width,
          backgroundColor: theme.colors.grey6,
          alignContent: "center",
          justifyContent: "center",
          alignItems: "stretch"
        }}
      >
        <TouchableOpacity
          containerStyle={{ flex: 1, minHeight: 12 }}
          style={{
            flex: 1
          }}
          onPress={() => reset()}
        />
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <TouchableOpacity
            containerStyle={{ flex: 1, flexShrink: 1 }}
            style={{
              flex: 1
            }}
            onPress={() => reset()}
          />
          <View
            style={{
              backgroundColor: "white",
              borderRadius: 10,
              justifyContent: "center",
              alignItems: "center",
              paddingHorizontal: 8,
              paddingVertical: 12,
              elevation: 8
            }}
          >
            <View
              style={{
                flexDirection: "row",
                alignItems: "baseline",
                justifyContent: "space-between",
                alignSelf: "stretch"
              }}
            >
              <Icon
                containerStyle={{ marginBottom: 8 }}
                size={20}
                name={
                  notification.variant === "success"
                    ? "check-circle-outline"
                    : notification.variant === "warning"
                    ? "alert-circle-outline"
                    : notification.variant === "error"
                    ? "close-circle-outline"
                    : notification.icon
                }
                color={
                  notification.variant === "success"
                    ? "green"
                    : notification.variant === "warning"
                    ? "yellow"
                    : notification.variant === "error"
                    ? "red"
                    : notification.variant === "error"
                    ? "blue"
                    : theme.colors.grey1
                }
              />
              <Text>{notification.title}</Text>
              <Icon
                containerStyle={{ marginBottom: 8, alignSelf: "flex-end" }}
                name="close"
                size={20}
                color={theme.colors.grey1}
                onPress={() => reset()}
              />
            </View>
            {notification.message && (
              <Text style={{ fontSize: 14 }}>{notification.message}</Text>
            )}
          </View>
          <TouchableOpacity
            containerStyle={{ flex: 1 }}
            style={{
              flex: 1
            }}
            onPress={() => reset()}
          />
        </View>
        <TouchableOpacity
          containerStyle={{ flex: 1, minHeight: 12 }}
          style={{
            flex: 1
          }}
          onPress={() => reset()}
        />
      </View>
    );
  } else {
    return null;
  }
};

const mapStateToProps = state => ({
  notification: state.notification
});

const mapDispatchToProps = dispatch => {
  return {
    reset: () => dispatch(resetNotification())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Notifier);
