const initialState = {
  notification: {
    show: false,
  },
  form: {
    show: false,
  },
};

export default function (state = initialState, action) {
  switch (action.type) {
    case "NOTIFY_USER":
      return {
        notification: {
          show: true,
          ...action.notification,
        },
      };
    case "ASK_USER_INPUT":
      return {
        form: {
          show: true,
          ...action.form,
        },
      };
    case "RESET_OVERLAY":
      return initialState;
    default:
      return state;
  }
}
