import React, { Component, useState } from "react";
import "react-native-gesture-handler";
import { Provider, useSelector, shallowEqual } from "react-redux";
import { Dimensions, View } from "react-native";
import { store } from "./store/index";
import { NavigationContainer, StackActions } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { ThemeProvider, Icon } from "react-native-elements";
import Constants from "expo-constants";
import Actvity from "./components/ActivityScreen";
import Post from "./components/PostScreen";
import Jobs from "./components/JobsScreen";
import Profile from "./components/ProfileScreen";
import People from "./components/PeopleScreen";
import Messages from "./components/MessagesScreen";
import LogIn from "./components/LogInScreen";
import SignUp from "./components/SignUpScreen";
import Notifier from "./components/Notifier";
import theme from "./Theme.json";
import navigationRef from "./RootNavigation";

const BottomTab = createBottomTabNavigator();
const Drawer = createDrawerNavigator();

const AuthStack = () => {
  const [signUp, setSignUp] = useState(false);
  const switchPanel = () => {
    setSignUp(!signUp);
  };
  if (signUp) {
    return <SignUp switchPanel={switchPanel} />;
  } else {
    return <LogIn switchPanel={switchPanel} />;
  }
};

const MainStack = () => {
  return (
    <BottomTab.Navigator
      initialRouteName="People"
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          switch (route.name) {
            case "Home":
              iconName = "home";
              break;
            case "Post":
              iconName = "plus-box";
              break;
            case "Jobs":
              iconName = "briefcase";
              break;
            case "People":
              iconName = "account-group";
              break;
            case "Messages":
              iconName = "forum";
              break;
            default:
              break;
          }

          return <Icon name={iconName} size={size} color={color} />;
        }
      })}
      tabBarOptions={{
        activeTintColor: theme.colors.primary,
        inactiveTintColor: "gray"
      }}
    >
      {/* <BottomTab.Screen name="Home" component={Home} /> */}
      <BottomTab.Screen
        name="People"
        component={People}
        options={{ title: "Alumnis" }}
      />
      <BottomTab.Screen
        name="Jobs"
        component={Jobs}
        options={{ title: "Emplois" }}
      />
      <BottomTab.Screen
        name="Post"
        component={Post}
        options={{ title: "Poster" }}
      />
      <BottomTab.Screen
        name="Messages"
        component={Messages}
        options={{ title: "Messages" }}
      />
    </BottomTab.Navigator>
  );
};

const AppStack = () => {
  const isLoggedIn = useSelector(state => state.auth.isLoggedIn, shallowEqual);

  if (isLoggedIn) {
    return (
      <Drawer.Navigator initialRouteName="Main" headerMode="screen">
        <Drawer.Screen
          name="Main"
          component={MainStack}
          options={{ title: "Home" }}
        />
        <Drawer.Screen
          name="Profile"
          component={Profile}
          options={{ title: "Mon profile" }}
        />
        <Drawer.Screen
          name="MyJobs"
          component={Actvity}
          options={{ title: "Mes offres d'emplois" }}
        />
      </Drawer.Navigator>
    );
  } else {
    return <AuthStack />;
  }
};

export default class App extends Component {
  render() {
    return (
      <Provider store={store}>
        <ThemeProvider theme={theme}>
          <View
            style={{
              flex: 1,
              height:
                Dimensions.get("window").height - Constants.statusBarHeight
            }}
          >
            <NavigationContainer ref={navigationRef}>
              <Notifier />
              <AppStack />
            </NavigationContainer>
          </View>
        </ThemeProvider>
      </Provider>
    );
  }
}
