import React, { useContext, useState } from "react";
import { connect, useDispatch } from "react-redux";
import { Text, View, Dimensions } from "react-native";
import { Icon, ThemeContext, Button } from "react-native-elements";
import { resetNotification } from "../actions/notifyActions";
import * as RootNavigation from "../RootNavigation.js";
import { TouchableOpacity } from "react-native-gesture-handler";
import { sendMessage } from "../actions/chatActions";

const Notifier = ({ notification, reset, menu }) => {
  const { theme } = useContext(ThemeContext);
  const [input, setInput] = useState("");
  const dispatch = useDispatch();
  notification.message &&
    setTimeout(() => {
      reset();
      notification.redirect &&
        RootNavigation.navigate(notification.redirect, {});
      notification.dispatchCallback &&
        dispatch({ type: notification.dispatchCallback });
    }, notification.timeout);

  console.log(menu);

  if (menu.show === true) {
    return (
      <View
        style={{
          position: "absolute",
          height: Dimensions.get("window").height,
          width: Dimensions.get("window").width,
          backgroundColor: theme.colors.grey6,
          alignContent: "center",
          justifyContent: "center",
          alignItems: "stretch",
        }}
      >
        <TouchableOpacity
          containerStyle={{ flex: 1, minHeight: 12 }}
          style={{
            flex: 1,
            minHeight: 12,
          }}
          onPress={() => reset()}
        />
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <TouchableOpacity
            containerStyle={{ flex: 1, minWidth: 12 }}
            style={{
              flex: 1,
              minWidth: 12,
            }}
            onPress={() => reset()}
          />
          <View
            style={{
              backgroundColor: "white",
              borderRadius: 10,
              justifyContent: "center",
              alignItems: "center",
              paddingHorizontal: 12,
              paddingVertical: 8,
              elevation: 8,
            }}
          >
            {menu.buttons.map((button) => {
              return (
                <Button
                  title={button.title}
                  onPress={dispatch(button.dispatch())}
                />
              );
            })}
            {menu.input && (
              <View
                style={{
                  flexDirection: "row",
                  backgroundColor: "white",
                  alignItems: "center",
                  justifyContent: "center",
                  alignContent: "center",
                  marginTop: 8,
                }}
              >
                <TextInput
                  value={input}
                  multiline
                  placeholder="Ecrivez ici."
                  placeholderTextColor="grey"
                  onChangeText={(text) => setInput(text)}
                  style={{
                    backgroundColor: theme.colors.grey5,
                    borderColor: theme.colors.grey4,
                    borderWidth: 1,
                    borderRadius: 5,
                    paddingVertical: 2,
                    paddingHorizontal: 4,
                    maxHeight: 80,
                  }}
                />
                <Button
                  containerStyle={{ height: 30 }}
                  icon={
                    <Icon
                      name="send"
                      size={20}
                      color={
                        newMessage ? theme.colors.primary : theme.colors.grey2
                      }
                    />
                  }
                  disabled={!newMessage}
                  type="clear"
                  onPress={() => {
                    dispatch(menu.input.action(input));
                  }}
                />
              </View>
            )}
            {menu.message && (
              <Text style={{ fontSize: 14 }}>{menu.message}</Text>
            )}
          </View>
          <TouchableOpacity
            containerStyle={{ flex: 1, minWidth: 12 }}
            style={{
              flex: 1,
              minWidth: 12,
            }}
            onPress={() => reset()}
          />
        </View>
      </View>
    );
  }

  if (notification.show === true) {
    return (
      <View
        style={{
          position: "absolute",
          height: Dimensions.get("window").height,
          width: Dimensions.get("window").width,
          backgroundColor: theme.colors.grey6,
          alignContent: "center",
          justifyContent: "center",
          alignItems: "stretch",
        }}
      >
        <TouchableOpacity
          containerStyle={{ flex: 1, minHeight: 12 }}
          style={{
            flex: 1,
            minHeight: 12,
          }}
          onPress={() => reset()}
        />
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <TouchableOpacity
            containerStyle={{ flex: 1, minWidth: 12 }}
            style={{
              flex: 1,
              minWidth: 12,
            }}
            onPress={() => reset()}
          />
          <View
            style={{
              backgroundColor: "white",
              borderRadius: 10,
              justifyContent: "center",
              alignItems: "center",
              paddingHorizontal: 12,
              paddingVertical: 8,
              elevation: 8,
            }}
          >
            <Icon
              containerStyle={{ marginBottom: 8 }}
              size={30}
              name={
                notification.variant === "success"
                  ? "check-circle-outline"
                  : notification.variant === "warning"
                  ? "alert-circle-outline"
                  : notification.variant === "error"
                  ? "close-circle-outline"
                  : notification.icon
              }
              color={
                notification.variant === "success"
                  ? "green"
                  : notification.variant === "warning"
                  ? "yellow"
                  : notification.variant === "error"
                  ? "red"
                  : notification.variant === "error"
                  ? "blue"
                  : theme.colors.grey1
              }
            />
            {notification.message && (
              <Text style={{ fontSize: 14 }}>{notification.message}</Text>
            )}
          </View>
          <TouchableOpacity
            containerStyle={{ flex: 1, minWidth: 12 }}
            style={{
              flex: 1,
              minWidth: 12,
            }}
            onPress={() => reset()}
          />
        </View>
        <TouchableOpacity
          containerStyle={{ flex: 1, minHeight: 12 }}
          style={{
            flex: 1,
            minHeight: 12,
          }}
          onPress={() => reset()}
        />
      </View>
    );
  }
  return null;
};

const mapStateToProps = (state) => ({
  notification: state.notification,
});

const mapDispatchToProps = (dispatch) => {
  return {
    reset: () => dispatch(resetNotification()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Notifier);
