export const showOverlay = ({ notification, form, menu }) => {
  return {
    type: "SHOW_OVERLAY",
    notification,
  };
};

export const askUserInput = (form) => {
  return {
    type: "ASK_USER_INPUT",
    form,
  };
};

export const resetOverlay = () => {
  return {
    type: "RESET_OVERLAY",
  };
};
