import React, { useState, useEffect, useCallback, useContext } from "react";
import { connect, useDispatch } from "react-redux";
import {
  View,
  Text,
  ActivityIndicator,
  FlatList,
  TouchableOpacity,
  RefreshControl,
  TextInput,
} from "react-native";
import {
  SearchBar,
  Avatar,
  ThemeContext,
  Button,
  Icon,
} from "react-native-elements";
import _ from "lodash";
import "moment/locale/fr";
import moment from "moment";
import { fetchUsers } from "../../actions/usersActions";
import {
  fetchConversations,
  sendMessage,
  deleteConversation,
} from "../../actions/chatActions";
import { apiConfig } from "../../config";
import { notifyUser, askUserInput } from "../../actions/overlayAction";

const SearchResultItem = ({ item, theme, onSendMessage, navigation, chat }) => {
  const [showMessageForm, setShowMessageForm] = useState(false);
  const [newMessage, setNewMessage] = useState("");
  const dispatch = useDispatch();
  const fullName =
    _.capitalize(item.firstname) + " " + _.capitalize(item.lastname);

  return (
    <TouchableOpacity
      onPress={
        chat.interlocutorsIds.includes(item._id)
          ? () =>
              navigation.navigate("Room", {
                title:
                  _.capitalize(item.firstname) +
                  " " +
                  _.capitalize(item.lastname),
                conversation: chat.convIdsWithPartsIds
                  .filter(
                    (element) =>
                      element.participants.includes(item._id) === true
                  )
                  .filter((conv) => conv.participants.length === 2)[0],
              })
          : () =>
              dispatch(
                askUserInput({
                  action: sendMessage,
                  message: "Message à l'intention de " + fullName,
                })
              )
      }
    >
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          paddingVertical: 4,
          paddingHorizontal: 8,
          borderBottomColor: theme.colors.grey5,
          borderBottomWidth: 1,
        }}
      >
        <View
          style={{
            alignContent: "center",
            justifyContent: "center",
            alignItems: "center",
            marginRight: 12,
          }}
        >
          {item.avatar ? (
            <Avatar
              source={{
                uri: `https://siee-gate.herokuapp.com/api/files/avatar/${item.avatar?.filename}`,
              }}
              size="small"
            />
          ) : (
            <Avatar
              size="small"
              title={item.firstname[0].concat(item.lastname[0]).toUpperCase()}
            />
          )}
        </View>
        {!showMessageForm ? (
          <Text style={{ paddingLeft: 12 }}>
            {_.capitalize(item.firstname) + " " + _.capitalize(item.lastname)}
          </Text>
        ) : (
          <View
            style={{
              flexDirection: "row",
              backgroundColor: "white",
              alignItems: "center",
              justifyContent: "center",
              alignContent: "center",
              marginTop: 8,
            }}
          >
            <TextInput
              value={newMessage}
              multiline
              placeholder="Ecrivez ici."
              placeholderTextColor="grey"
              onChangeText={(text) => setNewMessage(text)}
              style={{
                backgroundColor: theme.colors.grey5,
                borderColor: theme.colors.grey4,
                borderWidth: 1,
                borderRadius: 5,
                paddingVertical: 2,
                paddingHorizontal: 4,
                maxHeight: 80,
              }}
            />
            <Button
              containerStyle={{ height: 30 }}
              icon={
                <Icon
                  name="send"
                  size={20}
                  color={newMessage ? theme.colors.primary : theme.colors.grey2}
                />
              }
              disabled={!newMessage}
              type="clear"
              onPress={() => {
                onSendMessage(newMessage, item._id);
                setNewMessage("");
                setShowMessageForm(false);
              }}
            />
          </View>
        )}
      </View>
    </TouchableOpacity>
  );
};

const Item = ({
  item,
  navigation,
  auth,
  theme,
  deleteConversation,
  fetchConversations,
}) => {
  const [showActionMenu, setShowActionMenu] = useState(false);
  const [deleting, setDeleting] = useState(false);

  const listParticipants = () => {
    if (item.participants.length > 1) {
      const participantsList = item.participants.map((participant) =>
        _.capitalize(participant.firstname)
      );
      return participantsList.toString().replace(",", ", ");
    } else {
      return _.capitalize(item.participants[0].firstname).concat(
        " ",
        _.capitalize(item.participants[0].lastname)
      );
    }
  };

  const participants = listParticipants(item);

  return (
    <TouchableOpacity
      onPress={() =>
        navigation.navigate("Room", {
          conversation: item,
        })
      }
    >
      <View
        style={{
          paddingHorizontal: 8,
          paddingVertical: 8,
          borderBottomColor: "white",
          borderBottomWidth: 3,
          backgroundColor: "white",
        }}
      >
        <View style={{ flexDirection: "row" }}>
          <View
            style={{
              alignContent: "center",
              justifyContent: "center",
              paddingRight: 8,
            }}
          >
            {item.participants[0].avatar ? (
              <Avatar
                source={{
                  uri: `${apiConfig.baseUrl}/files/avatar/${item.participants[0].avatar?.filename}`,
                }}
                size="medium"
              />
            ) : (
              <Avatar
                size="medium"
                title={item.participants[0].firstname[0]
                  .concat(item.participants[0].lastname[0])
                  .toUpperCase()}
              />
            )}
          </View>
          <View
            style={{
              flex: 1,
              justifyContent: "space-between",
            }}
          >
            <View
              style={{
                flexDirection: "row",
                flex: 1,
                justifyContent: "space-between",
              }}
            >
              <Text
                style={{
                  textAlignVertical: "bottom",
                  fontSize: 16,
                  fontWeight: "bold",
                }}
              >
                {participants}
              </Text>
              <Text
                color="grey"
                style={{
                  color: "grey",
                  fontSize: 10,
                  textAlignVertical: "bottom",
                }}
              >
                {_.capitalize(
                  moment(item.lastMessage.sentAt).locale("fr").fromNow()
                )}
              </Text>
            </View>
            <View style={{ flexDirection: "row", alignItems: "center" }}>
              <View
                style={{ alignContent: "center", justifyContent: "center" }}
              >
                {item.lastMessage.sender === auth.user._id ? (
                  <Icon
                    name="arrow-top-right"
                    color={theme.colors.grey1}
                    size={14}
                  />
                ) : (
                  <Icon
                    name="arrow-bottom-left"
                    color={theme.colors.grey3}
                    size={14}
                  />
                )}
              </View>
              <Text
                style={{
                  textAlignVertical: "center",
                  fontSize: 14,
                  flex: 1,
                }}
              >
                {item.lastMessage.text}
              </Text>
              <View
                style={{
                  flexDirection: "row",
                  alignContent: "center",
                  justifyContent: "center",
                }}
              >
                <Icon
                  name="dots-horizontal"
                  style={{ alignSelf: "flex-end" }}
                  onPress={() => setShowActionMenu(!showActionMenu)}
                />
                {showActionMenu && (
                  <Icon
                    name="delete"
                    size={20}
                    color="black"
                    onPress={async () => {
                      setDeleting(true);
                      await deleteConversation(item._id);
                      setDeleting(false);
                      setShowActionMenu(false);
                      fetchConversations();
                    }}
                  />
                )}
              </View>
            </View>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const ChatLobbyScreen = ({
  navigation,
  fetchConversations,
  fetchUsers,
  chat,
  users,
  auth,
  sendMessage,
  deleteConversation,
}) => {
  const [refreshing, setRefreshing] = useState(false);
  const [search, setSearch] = useState("");
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    function fetchData() {
      fetchConversations();
      fetchUsers();
    }
    fetchData();
  }, []);

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    fetchConversations();
    setRefreshing(false);
  }, [refreshing]);

  const searchProcess = () => {
    const searchResults = [];
    users.users.forEach((user) => {
      if (user._id !== auth.user._id) {
        if (
          user.firstname.includes(search.toLowerCase()) === true ||
          user.lastname.includes(search.toLowerCase())
        ) {
          searchResults.push(user);
        }
      }
    });
    return searchResults;
  };

  const onSendMessage = async (newMessage, recipient) => {
    if (!newMessage) {
      return;
    }
    await sendMessage({
      message: {
        text: newMessage,
      },
      recipients: [recipient],
    });
    setSearch(null);
  };
  console.log(chat.conversations);

  return (
    <View style={{ flex: 1 }}>
      <View style={{ backgroundColor: "white", paddingVertical: 4 }}>
        <SearchBar
          placeholder="Chercher un alumni ici..."
          onChangeText={setSearch}
          value={search}
          lightTheme={true}
          platform="android"
          containerStyle={{
            paddingTop: 0,
            height: 40,
          }}
        />
        {users.isLoaded && (
          <FlatList
            data={search && searchProcess()}
            renderItem={({ item }) => (
              <SearchResultItem
                item={item}
                theme={theme}
                onSendMessage={onSendMessage}
                chat={chat}
                navigation={navigation}
              />
            )}
            keyExtractor={(item) => item._id}
          />
        )}
        {!users.isLoaded && (
          <View
            style={{
              flex: 1,
              alignContent: "center",
              justifyContent: "center",
            }}
          >
            <ActivityIndicator size="small" />
          </View>
        )}
      </View>
      {chat.isLoaded ? (
        chat.conversations.length > 0 ? (
          <FlatList
            data={chat.conversations}
            renderItem={({ item }) => (
              <Item
                item={item}
                auth={auth}
                navigation={navigation}
                theme={theme}
                deleteConversation={deleteConversation}
                fetchConversations={fetchConversations}
              />
            )}
            keyExtractor={(item) => item._id}
            refreshControl={
              <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
            }
          />
        ) : (
          <View
            style={{
              flex: 1,
              alignContent: "center",
              justifyContent: "center",
            }}
          >
            <Text style={{ textAlign: "center", color: theme.colors.grey3 }}>
              Aucune conversation
            </Text>
            <Button
              title="Rafraichir"
              type="clear"
              onPress={fetchConversations}
            />
          </View>
        )
      ) : (
        <View
          style={{
            flex: 1,
            alignContent: "center",
            justifyContent: "center",
          }}
        >
          <ActivityIndicator size="large" />
        </View>
      )}
    </View>
  );
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  chat: state.chat,
  users: state.users,
});

const mapDispatchToProps = (dispatch) => {
  return {
    fetchConversations: () => dispatch(fetchConversations()),
    fetchUsers: () => dispatch(fetchUsers()),
    sendMessage: (message) => dispatch(sendMessage(message)),
    deleteConversation: (conversation_id) =>
      dispatch(deleteConversation(conversation_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ChatLobbyScreen);
