import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  View,
  Text,
  ActivityIndicator,
  FlatList,
  TouchableOpacity,
} from "react-native";
import { Avatar, Icon } from "react-native-elements";
import theme from "../../Theme.json";
import { fetchUsers } from "../../actions/usersActions";
import _ from "lodash";
import { apiConfig } from "../../config";
import CustomInput from "../CustomInput";

const Item = ({ item, navigation }) => {
  if (!item) return null;
  return (
    <TouchableOpacity
      onPress={() => {
        navigation.navigate("Read", { id: item._id, item });
      }}
    >
      <View
        style={{
          paddingTop: 8,
          paddingHorizontal: 20,
          marginVertical: 4,
          marginHorizontal: 0,
          borderBottomWidth: 1,
          borderBottomColor: theme.colors.grey4,
          paddingVertical: 12,
          flexDirection: "row",
        }}
      >
        <View
          style={{
            alignContent: "center",
            justifyContent: "center",
            alignItems: "center",
            marginRight: 12,
          }}
        >
          {item.avatar ? (
            <Avatar
              source={{
                uri: `${apiConfig.baseUrl}/api/files/avatars/${item.avatar}`,
              }}
              size="medium"
            />
          ) : (
            <Avatar
              size="medium"
              title={item.firstname[0].concat(item.lastname[0]).toUpperCase()}
            />
          )}
        </View>
        <View>
          <Text
            style={{
              textAlignVertical: "bottom",
              fontWeight: "bold",
              fontSize: 16,
            }}
          >
            {_.capitalize(item.firstname)} {_.capitalize(item.lastname)}
          </Text>
          <View style={{ flexDirection: "row", flex: 1, alignItems: "center" }}>
            <Icon name="account-card-details" size={20} />
            <Text style={{ textAlignVertical: "bottom", marginStart: 8 }}>
              {item.jobTitle} chez {item.organisation}
            </Text>
          </View>
          <View style={{ flexDirection: "row", flex: 1, alignItems: "center" }}>
            <Icon name="school" size={20} />
            <Text style={{ textAlignVertical: "bottom", marginStart: 8 }}>
              SIEE promotion {item.promo}
            </Text>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const PeopleScreen = ({ navigation }) => {
  const dispatch = useDispatch();
  const { users } = useSelector((state) => state);

  useEffect(() => {
    function fetchData() {
      dispatch(fetchUsers());
    }
    fetchData();
  }, []);
  const [search, setSearch] = useState("");

  const searchableKeys = [
    "firstname",
    "lastname",
    "organisation",
    "city",
    "country",
    "jobTitle",
    "promo",
  ];

  const data = users.users?.filter((u) => {
    let a = false;
    searchableKeys.every((sk, i) => {
      if (`${u[sk]}`.toLowerCase().search(search.toLowerCase()) > -1) a = true;
      if (a === true) return false;
      else return true;
    });
    return a;
  });

  return (
    <View style={{ flex: 1 }}>
      <CustomInput
        onChangeText={setSearch}
        placeholder="John, Doe, Paris, KPMG..."
        value={search}
        leftIcon="magnify"
      />
      {useSelector((state) => state.users.isLoaded) ? (
        <FlatList
          style={{
            borderRadius: 0,
            shadowOffset: { width: 10, height: 10 },
            shadowColor: "black",
            shadowOpacity: 0.3,
            elevation: 2,
            backgroundColor: "white",
          }}
          data={data}
          renderItem={({ item }) => (
            <Item item={item} navigation={navigation} />
          )}
          keyExtractor={(item) => item._id}
        />
      ) : (
        <View
          style={{ flex: 1, alignContent: "center", justifyContent: "center" }}
        >
          <ActivityIndicator size="large" />
        </View>
      )}
    </View>
  );
};

export default PeopleScreen;
