export const showOverlay = ({
  notification,
  form,
  menu,
  timeout,
  callbacks,
  redirect,
}) => {
  return {
    type: "SHOW_OVERLAY",
    payload: {
      notification,
      form,
      menu,
      timeout,
      callbacks,
      redirect,
    },
  };
};

export const showNotification = (
  notification,
  timeout,
  callbacks,
  redirect
) => {
  return {
    type: "SHOW_OVERLAY",
    payload: {
      notification,
      timeout,
      callbacks,
      redirect,
    },
  };
};

export const showDateInput = () => {
  return {
    type: "SHOW_OVERLAY",
    payload: {
      dateInput,
      timeout,
      callbacks,
      redirect,
    },
  };
};

export const resetOverlay = () => {
  return {
    type: "RESET_OVERLAY",
  };
};
