const initialState = {
  conversations: [],
  conversationsIds: [],
  interlocutorsIds: [],
  lastUpdatedAt: null,
  isLoaded: false,
  isFetchingConversations: false,
  isFetchingConversation: false,
};

import _ from "lodash";

// {
//   data: {conversation_id: ""},
//   type: "RECEIVE_READ_ACK"
// }

const copyAndUpdate = (array, search, newItem) => {
  let updatedArray = [];
  let index = _.findIndex(state.conversations, search);

  if (index > 0) updatedArray.concat(array.slice(0, index));
  updatedArray.concat(updatedItem);
  updatedArray.concat(index + 1);

  return updatedArray;
};

export default function (state = initialState, action) {
  let i = _.findIndex(state.conversations, {
    _id: action.data.conversation_id,
  });

  switch (action.type) {
    case "REQUEST_CONVERSATIONS:SUCCESS":
      return {
        ...state,
        lastUpdatedAt: action.receivedAt,
        isLoaded: true,
        isFetchingConversations: false,
        ...action.data,
      };
    case "REQUEST_CONVERSATION:SUCCESS":
      return {
        ...state,
        conversations: returnUpdatedArray(
          state.conversations,
          i,
          action.data.conversation
        ),
        lastUpdatedAt: action.receivedAt,
        isLoaded: true,
      };
    case "RECEIVE_READ_ACK":
      return {
        ...state,
        lastUpdatedAt: action.receivedAt,
        conversations: [
          ...state.conversations.slice(0, i),
          {
            ...state.conversations[i],
            read: { user_id: action.data.user_id, sentAt: action.data.sentAt },
          },
          ...state.conversations.slice(i),
        ],
        isLoaded: true,
      };
    case "RECEIVE_WRITING_START":
    case "RECEIVE_WRITING_STOP":
      return {
        ...state,
        lastUpdatedAt: action.receivedAt,
        conversations: [
          {
            ...state.conversations[i],
            writing: action.wrintingData,
          },
          ...state.conversations.filter(
            (conv) => conv._id !== action.message.conversation_id
          ),
        ],
        isLoaded: true,
      };
    case "PRIVATE_CONVERSATION_ACK":
    case "RECEIVE_NEW_CONVERSATION":
      return {
        ...state,
        conversations: [action.conversation, ...state.conversations],
        lastUpdatedAt: action.receivedAt,
      };
    case "PRIVATE_MESSAGE_ACK":
    case "RECEIVE_PRIVATE_MESSAGE":
      if (state.conversations[i].messages) {
        return {
          ...state,
          lastUpdatedAt: action.receivedAt,
          conversations: [
            returnUpdatedArray(state.conversations, i, {
              ...state.conversations[i],
              lastMessage: action.message,
              messages: returnUpdatedArray(
                state.conversations[i].messages,
                action.message
              ),
            }),
          ],
          isLoaded: true,
        };
      } else {
        return {
          ...state,
          lastUpdatedAt: action.receivedAt,
          conversations: [
            {
              ...state.conversations[i],
              lastMessage: action.message,
              messages: [action.message],
            },
            ...state.conversations.filter(
              (conv) => conv._id !== action.message.conversation_id
            ),
          ],
          isLoaded: true,
        };
      }

    case "REQUEST_LOG_OUT:SUCCESS":
      return initialState;
    default:
      return state;
  }
}
