export const logIn = credentials => {
  return {
    type: "REQUEST_LOG_IN",
    route: "/auth/login",
    payload: credentials,
    method: "POST"
  };
};

export const signUp = credentials => {
  return {
    type: "REQUEST_SIGN_UP",
    route: "/auth/signup",
    payload: credentials,
    method: "POST"
  };
};

export const logOut = () => {
  return {
    type: "REQUEST_LOG_OUT",
    route: "/auth/logout",
    method: "GET"
  };
};

export const updateUser = user => {
  return {
    type: "REQUEST_UPDATE_USER",
    method: "POST",
    route: "auth/update",
    payload: user
  };
};

export const updateUserPassword = passwords => {
  return {
    type: "REQUEST_UPDATE_USER",
    method: "POST",
    route: "auth/password/update",
    payload: passwords
  };
};

export const deleteAccount = () => {
  return {
    type: "REQUEST_DELETE_ACCOUNT",
    method: "DELETE",
    route: "auth/delete"
  };
};
