import React, { useState, useContext } from "react";
import { connect } from "react-redux";
import { View, Text, StyleSheet, Image } from "react-native";
import { Input, Button, Icon, ThemeContext } from "react-native-elements";
import { logIn } from "../actions/authActions";
import { LinearGradient } from "expo-linear-gradient";

const logInScreen = ({ switchPanel, auth, logIn }) => {
  const { theme } = useContext(ThemeContext);
  const [email, setEmail] = useState("johndoe@gmail.com");
  const [password, setPassword] = useState("12345678");
  const [errorMessage, setErrorMessage] = useState("");

  const handleLogIn = async () => {
    {
      if (!email) {
        return setErrorMessage("Mél invalide.");
      }
      if (!password) {
        return setErrorMessage("Mot de passe invalide.");
      }
    }

    logIn({ email, password });
  };

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={[theme.colors.primary, theme.colors.secondary]}
        start={[0.25, 1]}
        end={[0, 0]}
        style={{
          flexDirection: "row",
          marginBottom: 20,
          height: 150,
          width: 150,
          borderRadius: 100
        }}
      >
        <Image
          source={require("../assets/logo.png")}
          style={{
            height: 150,
            width: 150,
            borderRadius: 100
          }}
        />
      </LinearGradient>
      <Input
        placeholder="Email"
        leftIcon={<Icon name="email" type="material-community" />}
        value={email}
        onChangeText={value => setEmail(value)}
        label="Email"
      />
      <Input
        placeholder="Mot de passe"
        leftIcon={<Icon name="lock" />}
        label="Mot de passe"
        secureTextEntry
        value={password}
        containerStyle={{ marginBottom: 30, marginTop: 8 }}
        onChangeText={value => setPassword(value)}
        errorStyle={{ color: "red" }}
        errorMessage={errorMessage}
      />
      <Button
        title="Se connecter"
        onPress={handleLogIn}
        loading={auth.isLoggingIn}
        containerStyle={{ marginBottom: 20, marginTop: 8 }}
      />
      <Button
        title="Nouveau? Je m'enregistre."
        type="clear"
        onPress={switchPanel}
        containerStyle={{ marginBottom: 30 }}
      />
    </View>
  );
};

const mapStateToProps = state => ({
  auth: state.auth
});

const mapDispatchToProps = dispatch => {
  return {
    logIn: credentials => dispatch(logIn(credentials))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(logInScreen);

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 12
  },
  textInput: {
    height: 40,
    width: "90%",
    borderColor: "gray",
    borderWidth: 1,
    marginTop: 8
  }
});
