import React, { useState } from "react";
import "react-native-gesture-handler";
import { Provider, useSelector, shallowEqual } from "react-redux";
import { View, SafeAreaView, Platform, StatusBar } from "react-native";
import { store } from "./store/index";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { ThemeProvider, Icon } from "react-native-elements";
import Actvity from "./components/MyJobsScreen";
import Post from "./components/PostScreen";
import Jobs from "./components/Jobs";
import Profile from "./components/Profile";
import People from "./components/People";
import Messages from "./components/Messages";
import LogIn from "./components/LogInScreen";
import SignUp from "./components/SignUpScreen";
import Notifier from "./components/Notifier";
import theme from "./Theme.json";
import { navigationRef, isMountedRef } from "./RootNavigation";

const BottomTab = createBottomTabNavigator();
const Drawer = createDrawerNavigator();

const AuthStack = () => {
  const [signUp, setSignUp] = useState(false);
  const switchPanel = () => {
    setSignUp(!signUp);
  };
  if (signUp) {
    return <SignUp switchPanel={switchPanel} />;
  } else {
    return <LogIn switchPanel={switchPanel} />;
  }
};

const MainStack = () => {
  return (
    <BottomTab.Navigator
      initialRouteName="People"
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          switch (route.name) {
            case "Home":
              iconName = "home";
              break;
            case "Post":
              iconName = "plus-box";
              break;
            case "Jobs":
              iconName = "briefcase";
              break;
            case "People":
              iconName = "account-group";
              break;
            case "Messages":
              iconName = "forum";
              break;
            default:
              break;
          }

          return <Icon name={iconName} size={size} color={color} />;
        }
      })}
      tabBarOptions={{
        activeTintColor: theme.colors.primary,
        inactiveTintColor: "gray"
      }}
    >
      <BottomTab.Screen
        name="People"
        component={People}
        options={{ title: "Alumnis" }}
      />
      <BottomTab.Screen
        name="Jobs"
        component={Jobs}
        options={{ title: "Emplois" }}
      />
      <BottomTab.Screen
        name="Post"
        component={Post}
        options={{ title: "Poster" }}
      />
      <BottomTab.Screen
        name="Messages"
        component={Messages}
        options={{ title: "Messages" }}
      />
    </BottomTab.Navigator>
  );
};

const AppStack = () => {
  const isLoggedIn = useSelector(state => state.auth.isLoggedIn, shallowEqual);
  return (
    <View
      style={{
        flex: 1,
        maxWidth: 500
      }}
    >
      {isLoggedIn ? (
        <Drawer.Navigator initialRouteName="Main" headerMode="screen">
          <Drawer.Screen
            name="Main"
            component={MainStack}
            options={{ title: "Acceuil" }}
          />
          <Drawer.Screen
            name="Profile"
            component={Profile}
            options={{ title: "Mon profile" }}
          />
          <Drawer.Screen
            name="MyJobs"
            component={Actvity}
            options={{ title: "Mes offres d'emplois" }}
          />
        </Drawer.Navigator>
      ) : (
        <AuthStack />
      )}
    </View>
  );
};

const App = () => {
  React.useEffect(() => {
    isMountedRef.current = true;

    return () => (isMountedRef.current = false);
  }, []);
  return (
    <Provider store={store}>
      <ThemeProvider theme={theme}>
        <SafeAreaView
          style={{
            flex: 1,
            flexDirection: "row",
            justifyContent: "center"
          }}
        >
          {Platform.OS === "ios" && <StatusBar barStyle="default" />}
          <NavigationContainer ref={navigationRef}>
            <AppStack />
            <Notifier />
          </NavigationContainer>
        </SafeAreaView>
      </ThemeProvider>
    </Provider>
  );
};
export default App;
