const initialState = {
  show: false,
  message: null
};

export default function(state = initialState, action) {
  switch (action.type) {
    case "NOTIFY_USER":
      return {
        ...action.notification,
        show: true
      };
    case "RESET_NOTIFICATION":
      return initialState;
    default:
      return state;
  }
}
