import React, { useState } from "react";
import "react-native-gesture-handler";
import { useSelector, shallowEqual } from "react-redux";
import { View } from "react-native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { Icon } from "react-native-elements";
import Actvity from "./components/MyJobsScreen";
import Post from "./components/PostScreen";
import Jobs from "./components/JobsScreen";
import Profile from "./components/ProfileScreen";
import People from "./components/PeopleScreen";
import Messages from "./components/MessagesScreen";
import LogIn from "./components/LogInScreen";
import SignUp from "./components/SignUpScreen";
import theme from "./Theme.json";

const BottomTab = createBottomTabNavigator();
const Drawer = createDrawerNavigator();

const AuthStack = () => {
  const [signUp, setSignUp] = useState(false);
  const switchPanel = () => {
    setSignUp(!signUp);
  };
  if (signUp) {
    return <SignUp switchPanel={switchPanel} />;
  } else {
    return <LogIn switchPanel={switchPanel} />;
  }
};

const MainStack = () => {
  return (
    <BottomTab.Navigator
      initialRouteName="People"
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          switch (route.name) {
            case "Home":
              iconName = "home";
              break;
            case "Post":
              iconName = "plus-box";
              break;
            case "Jobs":
              iconName = "briefcase";
              break;
            case "People":
              iconName = "account-group";
              break;
            case "Messages":
              iconName = "forum";
              break;
            default:
              break;
          }

          return <Icon name={iconName} size={size} color={color} />;
        }
      })}
      tabBarOptions={{
        activeTintColor: theme.colors.primary,
        inactiveTintColor: "gray"
      }}
    >
      <BottomTab.Screen
        name="People"
        component={People}
        options={{ title: "Alumnis" }}
      />
      <BottomTab.Screen
        name="Jobs"
        component={Jobs}
        options={{ title: "Emplois" }}
      />
      <BottomTab.Screen
        name="Post"
        component={Post}
        options={{ title: "Poster" }}
      />
      <BottomTab.Screen
        name="Messages"
        component={Messages}
        options={{ title: "Messages" }}
      />
    </BottomTab.Navigator>
  );
};

export default AppStack = () => {
  const isLoggedIn = useSelector(state => state.auth.isLoggedIn, shallowEqual);
  return (
    <View
      style={{
        alignSelf: "center",
        flex: 1,
        maxWidth: 500
      }}
    >
      {isLoggedIn ? (
        <Drawer.Navigator initialRouteName="Main" headerMode="screen">
          <Drawer.Screen
            name="Main"
            component={MainStack}
            options={{ title: "Acceuil" }}
          />
          <Drawer.Screen
            name="Profile"
            component={Profile}
            options={{ title: "Mon profile" }}
          />
          <Drawer.Screen
            name="MyJobs"
            component={Actvity}
            options={{ title: "Mes offres d'emplois" }}
          />
        </Drawer.Navigator>
      ) : (
        <AuthStack />
      )}
    </View>
  );
};
