const initialState = {
  isFetching: false,
  posts: [],
  lastUpdatedAt: null,
  isLoaded: false
};

export default function(state = initialState, action) {
  switch (action.type) {
    case "REQUEST_POSTS":
      return {
        ...state,
        isFetching: true
      };
    case "REQUEST_POSTS:SUCCESS":
      return {
        ...state,
        isFetching: false,
        lastUpdatedAt: action.receivedAt,
        isLoaded: true,
        ...action.data
      };
    case "REQUEST_POSTS:ERROR":
      return {
        ...state,
        isFetching: false,
        lastUpdatedAt: action.receivedAt,
        data: action.posts,
        isLoaded: true
      };
    default:
      return state;
  }
}
