import React, { useState, useContext } from "react";
import { connect } from "react-redux";
import {
  Icon,
  Button,
  Input,
  ThemeContext,
  Overlay
} from "react-native-elements";
import { View, ScrollView, Text } from "react-native";
import {
  updateUser,
  deleteAccount,
  updateUserPassword
} from "../actions/authActions";

const ProfileEditScreen = ({
  updateUser,
  deleteAccount,
  auth,
  updateUserPassword
}) => {
  const { theme } = useContext(ThemeContext);
  const [desc, setDesc] = useState(auth.user.description);
  const [email, setEmail] = useState(auth.user.email);
  const [jobTitle, setJobTitle] = useState(auth.user.jobTitle);
  const [organisation, setOrganisation] = useState(auth.user.organisation);
  const [promo, setPromo] = useState(`${auth.user.promo}`);
  const [password, setPassword] = useState("");
  const [confirmation, setConfirmation] = useState("");
  const [showPasswordForm, setShowPasswordForm] = useState(false);
  const [showPasswordEditForm, setShowPasswordEditForm] = useState(false);
  const [passwordError, setPasswordError] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const onEditProfile = () => {
    updateUser({
      ...auth.user,
      description: desc,
      email,
      jobTitle,
      organisation,
      promo
    });
  };

  const onChangePassword = () => {
    if (newPassword.length < 8) {
      return setPasswordError(
        "Choisissez un mot de passe d'au moins 8 caractères."
      );
    } else if (newPassword !== confirmation) {
      return setPasswordError("Les mots de passe ne correspondent pas.");
    }
    updateUserPassword(password, newPassword);
  };

  return (
    <View
      style={{
        flex: 1,
        alignContent: "space-between",
        paddingVertical: 8,
        paddingHorizontal: 8,
        backgroundColor: "white"
      }}
    >
      <ScrollView>
        <Input
          label="Promotion"
          value={promo}
          onChangeText={text => setPromo(text)}
          leftIcon={<Icon name="school" />}
        />
        <Input
          value={organisation}
          label="Organisation"
          onChangeText={text => setOrganisation(text)}
          leftIcon={<Icon name="domain" />}
        />
        <Input
          value={jobTitle}
          onChangeText={text => setJobTitle(text)}
          label="Profession"
          leftIcon={<Icon name="briefcase" />}
        />
        <Input
          label="Description"
          value={desc}
          multiline
          numberOfLines={8}
          onChangeText={text => setDesc(text)}
        />
        <Input
          label="email"
          value={email}
          leftIcon={<Icon name="email" />}
          onChangeText={text => setEmail(text)}
        />
      </ScrollView>
      <View style={{ paddingTop: 8 }}>
        {auth.updateUserError && (
          <Text style={{ color: "red", textAlign: "center" }}>
            {auth.updateUserError}
          </Text>
        )}
        <Button
          title="Valider les changements"
          onPress={() => onEditProfile()}
          buttonStyle={{ backgroundColor: theme.colors.success }}
        />
        <Button
          title="Changer mon mot de passe"
          onPress={() => setShowPasswordEditForm(true)}
          buttonStyle={{ backgroundColor: theme.colors.primary }}
        />
        <Button
          title="Supprimer mon compte"
          onPress={() => setShowPasswordForm(true)}
          buttonStyle={{ backgroundColor: theme.colors.error }}
        />
      </View>
      <Overlay
        isVisible={showPasswordEditForm}
        height="auto"
        onBackdropPress={() => setShowPasswordEditForm(false)}
      >
        <View>
          <Input
            placeholder="Mot de passe"
            leftIcon={<Icon name="lock" />}
            label="Ancien mot de passe"
            secureTextEntry
            value={password}
            leftIconContainerStyle={{ marginRight: 12 }}
            containerStyle={{ marginBottom: 20, marginTop: 8 }}
            onChangeText={value => setPassword(value)}
          />
          <Input
            placeholder="Mot de passe"
            leftIcon={<Icon name="lock" />}
            label="Nouveau mot de passe"
            secureTextEntry
            value={newPassword}
            leftIconContainerStyle={{ marginRight: 12 }}
            containerStyle={{ marginBottom: 20, marginTop: 8 }}
            onChangeText={value => {
              setPasswordError("");
              setNewPassword(value);
            }}
            errorMessage={passwordError}
          />
          <Input
            placeholder="Mot de passe"
            leftIcon={
              <Icon
                name={confirmation === newPassword ? "check" : "close"}
                color={confirmation === newPassword ? "green" : "red"}
              />
            }
            label="Confirmation"
            secureTextEntry
            value={confirmation}
            leftIconContainerStyle={{ marginRight: 12 }}
            containerStyle={{ marginBottom: 40, marginTop: 8 }}
            onChangeText={value => setConfirmation(value)}
          />
          <Button
            title="Valider"
            onPress={() => onChangePassword()}
            buttonStyle={{ backgroundColor: theme.colors.primary }}
            loading={auth.isUpdatingPassword}
          />
        </View>
      </Overlay>
      <Overlay
        isVisible={showPasswordForm}
        height="auto"
        onBackdropPress={() => setShowPasswordForm(false)}
      >
        <View>
          {auth.deleteAccountError && (
            <Text style={{ color: "red", textAlign: "center" }}>
              {auth.deleteAccountError}
            </Text>
          )}
          <Input
            placeholder="Mot de passe"
            leftIcon={<Icon name="lock" />}
            label="Mot de passe"
            secureTextEntry
            value={password}
            leftIconContainerStyle={{ marginRight: 12 }}
            containerStyle={{ marginBottom: 20, marginTop: 8 }}
            onChangeText={value => setPassword(value)}
          />
          <Button
            title="Supprimer mon compte"
            onPress={password => deleteAccount(password)}
            buttonStyle={{ backgroundColor: theme.colors.error }}
            loading={auth.isDeletingAccount}
          />
        </View>
      </Overlay>
    </View>
  );
};
const mapStateToProps = state => ({
  auth: state.auth
});

const mapDispatchToProps = dispatch => {
  return {
    updateUser: user => dispatch(updateUser(user)),
    deleteAccount: password => dispatch(deleteAccount(password)),
    updateUserPassword: (password, newPassword) =>
      dispatch(updateUserPassword(password, newPassword))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProfileEditScreen);
